            const content = addresses.map(addr => {
                const typeLabelKey = addr.addressType === 'permanent' ? 'permanentAddress' : 'currentAddress';
                const fullAddress = [
                    addr.addressLine1,
                    addr.addressLine2,
                    addr.subDistrict,
                    addr.district,
                    addr.province,
