                });
            }

            // Calculate days until expiry
            const today = new Date();
            const expiryDate = new Date(workPermit.expiryDate);
            const daysUntilExpiry = Math.floor((expiryDate - today) / (1000 * 60 * 60 * 24));

            // Determine status badge
            let statusBadge = '';
            if (daysUntilExpiry < 0) {
                // Expired
