 */
openAdvancedInfoModal(advancedInfo = null, nationality = 'Thai', gender = 'male') {
    const isThai = nationality === 'Thai';
    const isThaiMale = isThai && gender === 'male';

    ModalComponent.open({
        title: i18n.t('personal.editAdvancedInfo'),
        size: 'lg',
        content: `
