                    ToastComponent.success(i18n.t('toast.deleteSuccess'));
                    Router.refresh();
                } catch (error) {
                    ToastComponent.error(i18n.t('error.saveFailed'));
                }
            }
        },

        addDependent() {
            this.openDependentModal();
