     1â†’/**
     2â†’ * Role-Based Access Control (RBAC) Utilities
     3â†’ * Permission checking based on user role
     4â†’ */
     5â†’
     6â†’const RBAC = (function() {
     7â†’    // Role hierarchy (higher roles include permissions of lower roles)
     8â†’    const roleHierarchy = {
     9â†’        hr_manager: ['hr_admin', 'manager', 'employee'],
    10â†’        hr_admin: ['manager', 'employee'],
    11â†’        manager: ['employee'],
    12â†’        employee: []
    13â†’    };
    14â†’
    15â†’    // Permission definitions by role
    16â†’    const permissions = {
    17â†’        employee: [
    18â†’            'view_own_profile',
    19â†’            'view_own_employment',
    20â†’            'view_own_compensation',
    21â†’            'view_own_benefits',
    22â†’            'edit_own_contact',
    23â†’            'edit_own_emergency_contacts',
    24â†’            'add_own_emergency_contacts',
    25â†’            'delete_own_emergency_contacts',
    26â†’            'view_own_payslips',
    27â†’            'download_own_payslips',
    28â†’            'view_own_workflows',
    29â†’            'submit_own_workflows',
    30â†’            'view_org_chart'
    31â†’        ],
    32â†’        manager: [
    33â†’            'view_team_profiles',
    34â†’            'view_team_employment',
    35â†’            'view_team_org_chart',
    36â†’            'approve_workflows',
    37â†’            'reject_workflows',
    38â†’            'send_back_workflows',
    39â†’            'view_team_summary',
    40â†’            'view_pending_approvals'
    41â†’        ],
    42â†’        hr_admin: [
    43â†’            'view_all_profiles',
    44â†’            'edit_all_profiles',
    45â†’            'view_all_employment',
    46â†’            'edit_all_employment',
    47â†’            'view_all_compensation',
    48â†’            'view_all_benefits',
    49â†’            'manage_workflows',
    50â†’            'configure_workflows',
    51â†’            'view_reports',
    52â†’            'export_data'
    53â†’        ],
    54â†’        hr_manager: [
    55â†’            'approve_sensitive_workflows',
    56â†’            'view_salary_details',
    57â†’            'edit_compensation',
    58â†’            'manage_benefits',
    59â†’            'view_all_reports',
    60â†’            'manage_users',
    61â†’            'configure_system'
    62â†’        ]
    63â†’    };
    64â†’
    65â†’    // Field-level permissions (which fields can be edited by which roles)
    66â†’    const fieldPermissions = {
    67â†’        personalInfo: {
    68â†’            employee: ['nickname'],
    69â†’            manager: [],
    70â†’            hr_admin: ['salutation', 'firstName', 'lastName', 'gender', 'dateOfBirth', 'nationality', 'maritalStatus'],
    71â†’            hr_manager: ['all']
    72â†’        },
    73â†’        contactInfo: {
    74â†’            employee: ['personalEmail', 'personalMobile', 'homePhone'],
    75â†’            manager: [],
    76â†’            hr_admin: ['businessEmail', 'businessPhone'],
    77â†’            hr_manager: ['all']
    78â†’        },
    79â†’        address: {
    80â†’            employee: ['all'], // Requires approval
    81â†’            manager: [],
    82â†’            hr_admin: ['all'],
    83â†’            hr_manager: ['all']
    84â†’        },
    85â†’        emergencyContacts: {
    86â†’            employee: ['all'], // Self-service
    87â†’            manager: [],
    88â†’            hr_admin: ['all'],
    89â†’            hr_manager: ['all']
    90â†’        },
    91â†’        employment: {
    92â†’            employee: [],
    93â†’            manager: [],
    94â†’            hr_admin: ['all'],
    95â†’            hr_manager: ['all']
    96â†’        },
    97â†’        compensation: {
    98â†’            employee: [],
    99â†’            manager: [],
   100â†’            hr_admin: [],
   101â†’            hr_manager: ['all']
   102â†’        }
   103â†’    };
   104â†’
   105â†’    // Actions that require workflow approval
   106â†’    const workflowRequired = {
   107â†’        personalInfo: ['firstName', 'lastName', 'nationalId', 'dateOfBirth', 'nationality'],
   108â†’        address: ['all'],
   109â†’        bank: ['all'],
   110â†’        dependents: ['all']
   111â†’    };
   112â†’
   113â†’    // Self-service actions (no approval needed)
   114â†’    const selfService = {
   115â†’        contactInfo: ['personalEmail', 'personalMobile', 'homePhone'],
   116â†’        emergencyContacts: ['all']
   117â†’    };
   118â†’
   119â†’    /**
   120â†’     * Get all permissions for a role (including inherited)
   121â†’     */
   122â†’    function getAllPermissions(role) {
   123â†’        const allPerms = new Set(permissions[role] || []);
   124â†’
   125â†’        // Add inherited permissions
   126â†’        const inherited = roleHierarchy[role] || [];
   127â†’        for (const inheritedRole of inherited) {
   128â†’            const inheritedPerms = permissions[inheritedRole] || [];
   129â†’            inheritedPerms.forEach(p => allPerms.add(p));
   130â†’        }
   131â†’
   132â†’        return Array.from(allPerms);
   133â†’    }
   134â†’
   135â†’    /**
   136â†’     * Get current user from state
   137â†’     */
   138â†’    function getCurrentUser() {
   139â†’        return AppState?.get('currentUser') || null;
   140â†’    }
   141â†’
   142â†’    return {
   143â†’        /**
   144â†’         * Check if user has a specific permission
   145â†’         * @param {string} permission
   146â†’         * @param {object} user - Optional user object (defaults to current user)
   147â†’         * @returns {boolean}
   148â†’         */
   149â†’        hasPermission(permission, user = null) {
   150â†’            const currentUser = user || getCurrentUser();
   151â†’            if (!currentUser) return false;
   152â†’
   153â†’            const userPermissions = getAllPermissions(currentUser.role);
   154â†’            return userPermissions.includes(permission);
   155â†’        },
   156â†’
   157â†’        /**
   158â†’         * Check if user has any of the specified permissions
   159â†’         * @param {string[]} permissionList
   160â†’         * @param {object} user
   161â†’         * @returns {boolean}
   162â†’         */
   163â†’        hasAnyPermission(permissionList, user = null) {
   164â†’            return permissionList.some(p => this.hasPermission(p, user));
   165â†’        },
   166â†’
   167â†’        /**
   168â†’         * Check if user has all of the specified permissions
   169â†’         * @param {string[]} permissionList
   170â†’         * @param {object} user
   171â†’         * @returns {boolean}
   172â†’         */
   173â†’        hasAllPermissions(permissionList, user = null) {
   174â†’            return permissionList.every(p => this.hasPermission(p, user));
   175â†’        },
   176â†’
   177â†’        /**
   178â†’         * Check if user has a specific role
   179â†’         * @param {string} role
   180â†’         * @param {object} user
   181â†’         * @returns {boolean}
   182â†’         */
   183â†’        hasRole(role, user = null) {
   184â†’            const currentUser = user || getCurrentUser();
   185â†’            if (!currentUser) return false;
   186â†’
   187â†’            if (currentUser.role === role) return true;
   188â†’
   189â†’            // Check inherited roles
   190â†’            const inherited = roleHierarchy[currentUser.role] || [];
   191â†’            return inherited.includes(role);
   192â†’        },
   193â†’
   194â†’        /**
   195â†’         * Check if user can edit a specific field
   196â†’         * @param {string} section - Section name (personalInfo, contactInfo, etc.)
   197â†’         * @param {string} fieldName - Field name
   198â†’         * @param {object} user
   199â†’         * @returns {boolean}
   200â†’         */
   201â†’        canEditField(section, fieldName, user = null) {
   202â†’            const currentUser = user || getCurrentUser();
   203â†’            if (!currentUser) return false;
   204â†’
   205â†’            const sectionPerms = fieldPermissions[section];
   206â†’            if (!sectionPerms) return false;
   207â†’
   208â†’            // Check from highest role down
   209â†’            const roles = [currentUser.role, ...(roleHierarchy[currentUser.role] || [])];
   210â†’
   211â†’            for (const role of roles) {
   212â†’                const rolePerms = sectionPerms[role];
   213â†’                if (rolePerms) {
   214â†’                    if (rolePerms.includes('all') || rolePerms.includes(fieldName)) {
   215â†’                        return true;
   216â†’                    }
   217â†’                }
   218â†’            }
   219â†’
   220â†’            return false;
   221â†’        },
   222â†’
   223â†’        /**
   224â†’         * Check if a field change requires workflow approval
   225â†’         * @param {string} section
   226â†’         * @param {string} fieldName
   227â†’         * @returns {boolean}
   228â†’         */
   229â†’        requiresWorkflow(section, fieldName) {
   230â†’            const sectionWorkflow = workflowRequired[section];
   231â†’            if (!sectionWorkflow) return false;
   232â†’
   233â†’            return sectionWorkflow.includes('all') || sectionWorkflow.includes(fieldName);
   234â†’        },
   235â†’
   236â†’        /**
   237â†’         * Check if a field can be changed via self-service
   238â†’         * @param {string} section
   239â†’         * @param {string} fieldName
   240â†’         * @returns {boolean}
   241â†’         */
   242â†’        isSelfService(section, fieldName) {
   243â†’            const sectionSelfService = selfService[section];
   244â†’            if (!sectionSelfService) return false;
   245â†’
   246â†’            return sectionSelfService.includes('all') || sectionSelfService.includes(fieldName);
   247â†’        },
   248â†’
   249â†’        /**
   250â†’         * Check if user can view another employee's data
   251â†’         * @param {string} targetEmployeeId
   252â†’         * @param {object} user
   253â†’         * @returns {boolean}
   254â†’         */
   255â†’        canViewEmployee(targetEmployeeId, user = null) {
   256â†’            const currentUser = user || getCurrentUser();
   257â†’            if (!currentUser) return false;
