   261â†’
   262â†’            // HR roles can view all
   263â†’            if (this.hasRole('hr_admin', currentUser)) return true;
   264â†’
   265â†’            // Managers can view team members
   266â†’            if (this.hasPermission('view_team_profiles', currentUser)) {
   267â†’                // In real app, check if target is in team
   268â†’                return true;
   269â†’            }
   270â†’
   271â†’            return false;
   272â†’        },
