   274â†’        /**
   275â†’         * Check if user can approve workflows
   276â†’         * @param {string} workflowType
   277â†’         * @param {object} user
   278â†’         * @returns {boolean}
   279â†’         */
   280â†’        canApproveWorkflow(workflowType, user = null) {
   281â†’            const currentUser = user || getCurrentUser();
