    if (window.AccessibilityUtils && window.I18n) {
        window.AccessibilityUtils.announceToScreenReader(
            window.I18n.t('accessibility.modalClosed'),
            'polite'
        );
    }

    // Call onClose callback
    if (modalInfo.onClose) {
        modalInfo.onClose();
    }
},

