/**
 * Mobile Menu Component
 * Slide-out drawer navigation for mobile devices
 */

const MobileMenuComponent = (function() {
    let isOpen = false;
    let touchStartX = 0;
    let touchCurrentX = 0;
    let isDragging = false;

    return {
        /**
         * Render the mobile menu HTML
         * @returns {string}
         */
        render() {
            const user = AppState.get('currentUser');

            return `
                <!-- Mobile Menu Overlay -->
                <div id="mobile-menu-overlay"
                     class="mobile-menu-overlay hidden fixed inset-0 bg-black/50 z-40 lg:hidden"
                     onclick="MobileMenuComponent.close()"
                     role="presentation"
                     aria-hidden="true"></div>

                <!-- Mobile Menu Sidebar -->
                <nav id="mobile-menu"
                     class="mobile-menu closed fixed left-0 top-0 h-full w-80 max-w-[85vw] bg-white shadow-xl z-50 lg:hidden overflow-y-auto"
                     role="navigation"
                     aria-label="${i18n.t('accessibility.openMenu')}">

                    <!-- Menu Header -->
                    <div class="p-4 border-b bg-gradient-to-r from-cg-red to-red-700">
                        <div class="flex items-center justify-between">
                            <span class="text-lg font-bold text-white">
                                <span>CENTRAL</span><span class="opacity-90">GROUP</span>
                            </span>
                            <button class="p-2 hover:bg-white/20 rounded-full transition min-h-[44px] min-w-[44px] flex items-center justify-center"
                                    onclick="MobileMenuComponent.close()"
                                    aria-label="${i18n.t('accessibility.closeMenu')}">
                                <span class="material-icons text-white">close</span>
                            </button>
                        </div>

                        <!-- User Info in Mobile Menu -->
                        ${user ? `
                            <div class="mt-4 pt-4 border-t border-white/20">
                                <div class="flex items-center gap-3">
                                    <div class="w-12 h-12 bg-white rounded-full flex items-center justify-center text-cg-red font-bold text-lg">
                                        ${(user.username || 'U').substring(0, 2).toUpperCase()}
                                    </div>
                                    <div class="flex-1 min-w-0">
                                        <p class="font-medium text-white truncate">${user.username || 'User'}</p>
                                        <p class="text-sm text-white/80 truncate">${RBAC.getRoleDisplayName(user.role)}</p>
                                    </div>
                                </div>
                            </div>
                        ` : ''}
                    </div>

                    <!-- Navigation Links -->
                    <div class="p-4">
                        <div class="space-y-1">
                            <!-- Home -->
                            <a href="#/home"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px]"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-gray-600">home</span>
                                <span class="text-gray-800 font-medium">${i18n.t('nav.home')}</span>
                            </a>

                            <!-- My Profile -->
                            <a href="#/profile"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px]"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-gray-600">person</span>
                                <span class="text-gray-800 font-medium">${i18n.t('nav.profile')}</span>
                            </a>

                            <!-- Personal Info -->
                            <a href="#/profile/personal-info"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px] ml-4"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-sm text-gray-500">badge</span>
                                <span class="text-gray-700">${i18n.t('profile.personal')}</span>
                            </a>

                            <!-- Employment -->
                            <a href="#/profile/employment"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px] ml-4"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-sm text-gray-500">work</span>
                                <span class="text-gray-700">${i18n.t('profile.employment')}</span>
                            </a>

                            <!-- Compensation -->
                            <a href="#/profile/compensation"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px] ml-4"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-sm text-gray-500">payments</span>
                                <span class="text-gray-700">${i18n.t('profile.compensation')}</span>
                            </a>

                            <!-- Benefits -->
                            <a href="#/profile/benefits"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px] ml-4"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-sm text-gray-500">health_and_safety</span>
                                <span class="text-gray-700">${i18n.t('profile.benefits')}</span>
                            </a>

                            <!-- Workflows -->
                            <a href="#/workflows"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px]"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-gray-600">assignment</span>
                                <span class="text-gray-800 font-medium">${i18n.t('nav.workflows')}</span>
                            </a>
                        </div>

                        <hr class="my-4">

                        <!-- Settings & Language -->
                        <div class="space-y-1">
                            <!-- Settings -->
                            <a href="#/settings"
                               class="mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px]"
                               onclick="MobileMenuComponent.handleNavigation(event)">
                                <span class="material-icons text-gray-600">settings</span>
                                <span class="text-gray-800">${i18n.t('nav.settings')}</span>
                            </a>

                            <!-- Language Toggle -->
                            <button class="w-full mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-gray-100 rounded-lg transition min-h-[44px]"
                                    onclick="MobileMenuComponent.toggleLanguage()">
                                <span class="material-icons text-gray-600">language</span>
                                <span class="text-gray-800">${i18n.isThai() ? 'Switch to English' : 'เปลี่ยนเป็นภาษาไทย'}</span>
                                <span class="ml-auto text-sm font-medium text-cg-red">${i18n.isThai() ? 'EN' : 'TH'}</span>
                            </button>

                            <!-- Logout -->
                            <button class="w-full mobile-menu-item flex items-center gap-3 px-4 py-3 hover:bg-red-50 rounded-lg transition text-red-600 min-h-[44px]"
                                    onclick="MobileMenuComponent.logout()">
                                <span class="material-icons">logout</span>
                                <span class="font-medium">${i18n.t('nav.logout')}</span>
                            </button>
                        </div>
                    </div>
                </nav>
            `;
        },

        /**
         * Initialize mobile menu event listeners
         */
        init() {
            // Subscribe to language changes for re-rendering
            i18n.subscribe(() => {
                if (isOpen) {
                    this.updateContent();
                }
            });

            // Add keyboard event listener for Escape key
            document.addEventListener('keydown', (e) => {
                if (e.key === 'Escape' && isOpen) {
                    this.close();
                }
            });

            // Render initial content
            const container = document.getElementById('mobile-menu-container');
            if (container) {
                container.innerHTML = this.render();
                this.attachGestureListeners();
            }
        },

        /**
         * Attach touch gesture listeners for swipe-to-close
         */
        attachGestureListeners() {
            const menu = document.getElementById('mobile-menu');
            if (!menu) return;

            menu.addEventListener('touchstart', (e) => {
                touchStartX = e.touches[0].clientX;
                touchCurrentX = touchStartX;
                isDragging = false;
            }, { passive: true });

            menu.addEventListener('touchmove', (e) => {
                if (!isOpen) return;

                touchCurrentX = e.touches[0].clientX;
                const diff = touchCurrentX - touchStartX;

                // Only allow left swipe (to close)
                if (diff < -10) {
                    isDragging = true;
                    const translateX = Math.min(0, diff);
                    menu.style.transform = `translateX(${translateX}px)`;
                }
            }, { passive: true });

            menu.addEventListener('touchend', (e) => {
                if (!isDragging || !isOpen) {
                    menu.style.transform = '';
                    return;
                }

                const diff = touchCurrentX - touchStartX;

                // If swiped more than 50px to the left, close menu
                if (diff < -50) {
                    this.close();
                } else {
                    // Reset position
                    menu.style.transform = '';
                }

                isDragging = false;
            }, { passive: true });
        },

        /**
         * Open mobile menu
         */
        open() {
            isOpen = true;
            const menu = document.getElementById('mobile-menu');
            const overlay = document.getElementById('mobile-menu-overlay');
            const btn = document.getElementById('mobile-menu-btn');

            if (menu && overlay) {
                menu.classList.remove('closed');
                menu.classList.add('open');
                overlay.classList.remove('hidden');
                document.body.style.overflow = 'hidden';

                // Set ARIA attributes
                menu.setAttribute('aria-hidden', 'false');
                overlay.setAttribute('aria-hidden', 'false');
            }

            if (btn) {
                btn.setAttribute('aria-expanded', 'true');
            }
        },

        /**
         * Close mobile menu
         */
        close() {
            isOpen = false;
            const menu = document.getElementById('mobile-menu');
            const overlay = document.getElementById('mobile-menu-overlay');
            const btn = document.getElementById('mobile-menu-btn');

            if (menu && overlay) {
                menu.classList.add('closed');
                menu.classList.remove('open');
                overlay.classList.add('hidden');
                document.body.style.overflow = '';
                menu.style.transform = ''; // Reset any gesture transforms

                // Set ARIA attributes
                menu.setAttribute('aria-hidden', 'true');
                overlay.setAttribute('aria-hidden', 'true');
            }

            if (btn) {
                btn.setAttribute('aria-expanded', 'false');
            }
        },

        /**
         * Toggle mobile menu
         */
        toggle() {
            if (isOpen) {
                this.close();
            } else {
                this.open();
            }
        },

        /**
         * Handle navigation link clicks
         * @param {Event} event
         */
        handleNavigation(event) {
            // Close menu after navigation
            this.close();
        },

        /**
         * Toggle language and update menu
         */
        toggleLanguage() {
            i18n.toggleLanguage();
            ToastComponent.success(i18n.isThai() ? 'เปลี่ยนเป็นภาษาไทยแล้ว' : 'Switched to English');
            this.updateContent();
        },

        /**
         * Update menu content after language change
         */
        updateContent() {
            const container = document.getElementById('mobile-menu-container');
            if (container) {
                container.innerHTML = this.render();
                this.attachGestureListeners();
                if (isOpen) {
                    // Reopen menu if it was open
                    const menu = document.getElementById('mobile-menu');
                    const overlay = document.getElementById('mobile-menu-overlay');
                    if (menu && overlay) {
                        menu.classList.remove('closed');
                        menu.classList.add('open');
                        overlay.classList.remove('hidden');
                    }
                }
            }
        },

        /**
         * Handle logout
         */
        logout() {
            this.close();
            HeaderComponent.logout();
        },

        /**
         * Get current state
         * @returns {boolean}
         */
        isOpen() {
            return isOpen;
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = MobileMenuComponent;
}

/**
 * Header Component
 * Main navigation header with logo, search, icons
 */

const HeaderComponent = (function() {
    return {
        /**
         * Render the header
         * @returns {string}
         */
        render() {
            const user = AppState.get('currentUser');
            const unreadCount = AppState.getUnreadCount();

            return `
                <header class="bg-white shadow-md sticky top-0 z-40">
                    <div class="flex items-center justify-between px-4 py-2">
                        <!-- Logo and Menu -->
                        <div class="flex items-center gap-4">
                            <!-- Mobile Menu Button -->
                            <button class="lg:hidden p-2 hover:bg-gray-100 rounded-lg min-h-[44px] min-w-[44px]"
                                    onclick="MobileMenuComponent.toggle()"
                                    aria-label="${i18n.t('accessibility.openMenu')}"
                                    aria-expanded="false"
                                    id="mobile-menu-btn">
                                <span class="material-icons">menu</span>
                            </button>

                            <!-- Logo -->
                            <a href="#/home" class="flex items-center gap-2">
                                <div class="text-xl font-bold">
                                    <span class="text-cg-red">CENTRAL</span><span class="text-gray-800">GROUP</span>
                                </div>
                            </a>

                            <!-- Employee Files Dropdown -->
                            <div class="relative hidden md:block">
                                <button class="flex items-center gap-1 px-3 py-2 hover:bg-gray-100 rounded-lg transition"
                                        onclick="HeaderComponent.toggleDropdown('nav-dropdown')">
                                    <span class="text-gray-700">${i18n.t('nav.employeeFiles')}</span>
                                    <span class="material-icons text-sm text-gray-500">expand_more</span>
                                </button>
                                <div id="nav-dropdown" class="dropdown-menu absolute left-0 top-full mt-1 w-48 bg-white rounded-lg shadow-lg border py-1">
                                    <a href="#/home" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">home</span>
                                        <span>${i18n.t('nav.home')}</span>
                                    </a>
                                    <a href="#/profile" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">person</span>
                                        <span>${i18n.t('nav.profile')}</span>
                                    </a>
                                    <a href="#/workflows" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">assignment</span>
                                        <span>${i18n.t('nav.workflows')}</span>
                                    </a>
                                </div>
                            </div>
                        </div>

                        <!-- Search Bar -->
                        <div class="flex-1 max-w-xl mx-4 hidden md:block">
                            <div class="relative">
                                <span class="material-icons absolute left-3 top-2.5 text-gray-400">search</span>
                                <input type="text"
                                       placeholder="${i18n.t('nav.searchPlaceholder')}"
                                       class="w-full pl-10 pr-4 py-2 border rounded-full bg-gray-50 focus:outline-none focus:ring-2 focus:ring-cg-info focus:bg-white transition"
                                       aria-label="${i18n.t('common.search')}"
                                       onkeydown="if(event.key==='Enter') HeaderComponent.handleSearch(this.value)">
                            </div>
                        </div>

                        <!-- Action Icons -->
                        <div class="flex items-center gap-1">
                            <!-- Search Icon (Mobile) -->
                            <button class="md:hidden p-2 hover:bg-gray-100 rounded-full"
                                    onclick="HeaderComponent.toggleMobileSearch()">
                                <span class="material-icons text-gray-600">search</span>
                            </button>

                            <!-- Notifications -->
                            ${NotificationBellComponent.render(unreadCount)}

                            <!-- Help -->
                            <button class="hidden sm:block p-2 hover:bg-gray-100 rounded-full" title="${i18n.t('nav.help')}">
                                <span class="material-icons text-gray-600">help_outline</span>
                            </button>

                            <!-- Settings -->
                            <button class="hidden sm:block p-2 hover:bg-gray-100 rounded-full" title="${i18n.t('nav.settings')}">
                                <span class="material-icons text-gray-600">settings</span>
                            </button>

                            <!-- Language Toggle -->
                            <button class="p-2 hover:bg-gray-100 rounded-full"
                                    onclick="HeaderComponent.toggleLanguage()"
                                    title="${i18n.isThai() ? 'Switch to English' : 'เปลี่ยนเป็นภาษาไทย'}">
                                <span class="text-sm font-medium text-gray-600">${i18n.isThai() ? 'EN' : 'TH'}</span>
                            </button>

                            <!-- User Avatar -->
                            <div class="relative ml-2">
                                <button class="flex items-center gap-2 p-1 hover:bg-gray-100 rounded-full"
                                        onclick="HeaderComponent.toggleDropdown('user-dropdown')">
                                    <div class="w-8 h-8 bg-cg-red rounded-full flex items-center justify-center text-white font-medium text-sm">
                                        ${user ? (user.username || 'U').substring(0, 2).toUpperCase() : 'U'}
                                    </div>
                                    <span class="material-icons text-sm text-gray-500 hidden sm:block">expand_more</span>
                                </button>
                                <div id="user-dropdown" class="dropdown-menu absolute right-0 top-full mt-1 w-48 bg-white rounded-lg shadow-lg border py-1">
                                    <div class="px-4 py-2 border-b">
                                        <p class="font-medium text-gray-900">${user?.username || 'User'}</p>
                                        <p class="text-sm text-gray-500">${RBAC.getRoleDisplayName(user?.role)}</p>
                                    </div>
                                    <a href="#/profile" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">person</span>
                                        <span>${i18n.t('nav.profile')}</span>
                                    </a>
                                    <a href="#/settings" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">settings</span>
                                        <span>${i18n.t('nav.settings')}</span>
                                    </a>
                                    <hr class="my-1">
                                    <button class="w-full flex items-center gap-2 px-4 py-2 hover:bg-gray-50 text-red-600"
                                            onclick="HeaderComponent.logout()">
                                        <span class="material-icons text-sm">logout</span>
                                        <span>${i18n.t('nav.logout')}</span>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                    <!-- Mobile Search (Hidden by default) -->
                    <div id="mobile-search" class="hidden px-4 pb-3 md:hidden">
                        <div class="relative">
                            <span class="material-icons absolute left-3 top-2.5 text-gray-400">search</span>
                            <input type="text"
                                   placeholder="${i18n.t('nav.searchPlaceholder')}"
                                   class="w-full pl-10 pr-4 py-2 border rounded-full bg-gray-50 focus:outline-none focus:ring-2 focus:ring-cg-info"
                                   onkeydown="if(event.key==='Enter') HeaderComponent.handleSearch(this.value)">
                        </div>
                    </div>
                </header>
            `;
        },

        /**
         * Initialize header event listeners
         */
        init() {
            // Subscribe to notification changes
            AppState.subscribe('notifications', () => {
                this.updateNotificationCount();
            });
        },

        /**
         * Toggle dropdown visibility
         * @param {string} id
         */
        toggleDropdown(id) {
            const dropdown = document.getElementById(id);
            if (dropdown) {
                dropdown.classList.toggle('open');
            }
        },

        /**
         * Toggle mobile search
         */
        toggleMobileSearch() {
            const search = document.getElementById('mobile-search');
            if (search) {
                search.classList.toggle('hidden');
                if (!search.classList.contains('hidden')) {
                    search.querySelector('input').focus();
                }
            }
        },

        /**
         * Handle search
         * @param {string} query
         */
        handleSearch(query) {
            if (query.trim()) {
                console.log('Search:', query);
                // Implement search functionality
                ToastComponent.info(`Searching for: ${query}`);
            }
        },

        /**
         * Toggle language
         */
        toggleLanguage() {
            i18n.toggleLanguage();
            ToastComponent.success(i18n.isThai() ? 'เปลี่ยนเป็นภาษาไทยแล้ว' : 'Switched to English');
        },

        /**
         * Update notification count in header
         */
        updateNotificationCount() {
            const count = AppState.getUnreadCount();
            const badge = document.getElementById('notification-badge');
            if (badge) {
                badge.textContent = count;
                badge.classList.toggle('hidden', count === 0);
            }
        },

        /**
         * Logout handler
         */
        logout() {
            ModalComponent.confirm({
                title: i18n.t('nav.logout'),
                message: i18n.isThai() ? 'คุณต้องการออกจากระบบหรือไม่?' : 'Are you sure you want to logout?',
                confirmText: i18n.t('nav.logout'),
                icon: 'logout'
            }).then(confirmed => {
                if (confirmed) {
                    // Clear state and redirect
                    AppState.reset();
                    ToastComponent.success(i18n.isThai() ? 'ออกจากระบบแล้ว' : 'Logged out successfully');
                    // In real app, redirect to login page
                    Router.navigate('home');
                }
            });
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = HeaderComponent;
}

