   324→            }
   325→        },
   326→
   327→        /**
   328→         * Format file size
   329→         * @param {number} bytes
   330→         * @returns {string}
   331→         */
   332→        formatFileSize(bytes) {
   333→            if (bytes === 0) return '0 Bytes';
   334→            const k = 1024;
   335→            const sizes = ['Bytes', 'KB', 'MB', 'GB'];
   336→            const i = Math.floor(Math.log(bytes) / Math.log(k));
   337→            return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
   338→        },
   339→
   340→        /**
   341→         * Escape HTML to prevent XSS
   342→         * @param {string} str
   343→         * @returns {string}
