   345→        escapeHtml(str) {
   346→            if (!str) return '';
   347→            const div = document.createElement('div');
   348→            div.textContent = str;
   349→            return div.innerHTML;
   350→        },
   351→
   352→        /**
   353→         * Get form data as object
   354→         * @param {string} formId
   355→         * @returns {object}
   356→         */
   357→        getFormData(formId) {
   358→            const form = document.getElementById(formId);
   359→            if (!form) return {};
   360→
   361→            const formData = new FormData(form);
   362→            const data = {};
   363→
   364→            formData.forEach((value, key) => {
   365→                if (data[key]) {
   366→                    if (!Array.isArray(data[key])) {
   367→                        data[key] = [data[key]];
   368→                    }
   369→                    data[key].push(value);
   370→                } else {
   371→                    data[key] = value;
   372→                }
   373→            });
   374→
   375→            return data;
   376→        },
   377→
   378→        /**
   379→         * Set form field value
   380→         * @param {string} fieldId
   381→         * @param {*} value
   382→         */
   383→        setValue(fieldId, value) {
   384→            const field = document.getElementById(fieldId);
   385→            if (!field) return;
   386→
   387→            if (field.type === 'checkbox') {
   388→                field.checked = Boolean(value);
   389→            } else {
   390→                field.value = value || '';
   391→            }
   392→        },
   393→
   394→        /**
   395→         * Clear form errors
   396→         * @param {string} formId
   397→         */
   398→        clearErrors(formId) {
   399→            const form = document.getElementById(formId);
   400→            if (!form) return;
   401→
   402→            form.querySelectorAll('.error').forEach(el => {
   403→                el.classList.remove('error', 'border-red-500');
   404→                el.classList.add('border-gray-300');
   405→            });
   406→
   407→            form.querySelectorAll('[role="alert"]').forEach(el => {
   408→                el.remove();
   409→            });
   410→        },
   411→
   412→        /**
   413→         * Display form errors
   414→         * @param {object} errors - { fieldName: [errorMessages] }
   415→         */
   416→        showErrors(errors) {
   417→            Object.entries(errors).forEach(([fieldName, messages]) => {
   418→                const field = document.getElementById(fieldName);
   419→                if (field) {
   420→                    field.classList.add('error', 'border-red-500');
   421→                    field.classList.remove('border-gray-300');
   422→
   423→                    // Add error message
   424→                    const existingError = field.parentElement.querySelector('[role="alert"]');
   425→                    if (existingError) existingError.remove();
   426→
   427→                    const errorEl = document.createElement('p');
   428→                    errorEl.className = 'mt-1 text-xs text-red-500';
   429→                    errorEl.setAttribute('role', 'alert');
   430→                    errorEl.textContent = messages[0];
   431→                    field.parentElement.appendChild(errorEl);
   432→                }
   433→            });
   434→        }
   435→    };
   436→})();
   437→
   438→// Export for module usage
   439→if (typeof module !== 'undefined' && module.exports) {
   440→    module.exports = FormFieldComponent;
   441→}
