                    ${error ? `<p id="${fieldId}-error" class="mt-1 text-xs text-red-500" role="alert">${error}</p>` : ''}
                </div>
            `;
        },

        /**
         * Render a date picker
         * @param {object} options
         * @returns {string}
         */
        date(options) {
            const {
                id,
                name,
                label,
                value = '',
                required = false,
                disabled = false,
                error = '',
                hint = '',
                min = '',
                max = '',
                className = ''
            } = options;

            const fieldId = id || name;

            return `
                <div class="form-group ${className}">
                    <label for="${fieldId}" class="block text-sm font-medium text-gray-700 mb-1">
                        ${label}
                        ${required ? '<span class="text-red-500">*</span>' : ''}
                    </label>
                    <input
                        type="date"
                        id="${fieldId}"
                        name="${name}"
                        value="${value}"
                        ${required ? 'required aria-required="true"' : ''}
                        ${disabled ? 'disabled' : ''}
                        ${min ? `min="${min}"` : ''}
                        ${max ? `max="${max}"` : ''}
                        class="w-full px-3 py-2 border rounded-lg form-input
                               ${error ? 'border-red-500 error' : 'border-gray-300'}
                               ${disabled ? 'bg-gray-100 cursor-not-allowed' : ''}
                               focus:outline-none focus:ring-2 focus:ring-cg-info/20 focus:border-cg-info"
                        aria-describedby="${[error ? `${fieldId}-error` : '', hint ? `${fieldId}-hint` : ''].filter(Boolean).join(' ').trim() || undefined}"
                        aria-invalid="${error ? 'true' : 'false'}"
                    >
                    ${hint && !error ? `<p id="${fieldId}-hint" class="mt-1 text-xs text-gray-500">${hint}</p>` : ''}
                    ${error ? `<p id="${fieldId}-error" class="mt-1 text-xs text-red-500" role="alert">${error}</p>` : ''}
                </div>
            `;
        },

        /**
         * Render a checkbox
         * @param {object} options
         * @returns {string}
         */
        checkbox(options) {
            const {
                id,
                name,
                label,
                checked = false,
                disabled = false,
                error = '',
                className = ''
            } = options;

            const fieldId = id || name;

            return `
                <div class="form-group ${className}">
                    <label class="flex items-center gap-2 cursor-pointer ${disabled ? 'opacity-50 cursor-not-allowed' : ''}">
                        <input
                            type="checkbox"
                            id="${fieldId}"
                            name="${name}"
                            ${checked ? 'checked' : ''}
                            ${disabled ? 'disabled' : ''}
                            class="w-4 h-4 text-cg-red border-gray-300 rounded focus:ring-cg-red"
                        >
                        <span class="text-sm text-gray-700">${label}</span>
                    </label>
                    ${error ? `<p class="mt-1 text-xs text-red-500" role="alert">${error}</p>` : ''}
                </div>
            `;
        },

        /**
         * Render a file upload field
         * @param {object} options
         * @returns {string}
         */
        file(options) {
            const {
                id,
                name,
                label,
                accept = '',
                required = false,
                multiple = false,
                error = '',
                hint = '',
                className = ''
            } = options;

            const fieldId = id || name;

            return `
                <div class="form-group ${className}">
                    <label class="block text-sm font-medium text-gray-700 mb-1">
                        ${label}
                        ${required ? '<span class="text-red-500">*</span>' : ''}
                    </label>
                    <div class="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center hover:border-cg-info transition cursor-pointer"
                         onclick="document.getElementById('${fieldId}').click()">
                        <input
                            type="file"
                            id="${fieldId}"
                            name="${name}"
                            ${accept ? `accept="${accept}"` : ''}
                            ${required ? 'required' : ''}
                            ${multiple ? 'multiple' : ''}
                            class="hidden"
                            onchange="FormFieldComponent.handleFileSelect(this, '${fieldId}-preview')"
                        >
                        <span class="material-icons text-3xl text-gray-400 mb-2">cloud_upload</span>
                        <p class="text-sm text-gray-600">${i18n.t('common.dragDrop')}</p>
                        ${hint ? `<p class="text-xs text-gray-500 mt-1">${hint}</p>` : ''}
                    </div>
                    <div id="${fieldId}-preview" class="mt-2 space-y-2"></div>
                    ${error ? `<p class="mt-1 text-xs text-red-500" role="alert">${error}</p>` : ''}
                </div>
            `;
        },

        /**
         * Handle file selection preview
         * @param {HTMLInputElement} input
         * @param {string} previewId
         */
        handleFileSelect(input, previewId) {
            const preview = document.getElementById(previewId);
            if (!preview) return;

            preview.innerHTML = '';

            if (input.files) {
                Array.from(input.files).forEach(file => {
                    const fileItem = document.createElement('div');
                    fileItem.className = 'flex items-center gap-2 p-2 bg-gray-50 rounded';
                    fileItem.innerHTML = `
                        <span class="material-icons text-gray-500">attach_file</span>
                        <span class="flex-1 text-sm truncate">${file.name}</span>
                        <span class="text-xs text-gray-500">${this.formatFileSize(file.size)}</span>
                    `;
                    preview.appendChild(fileItem);
                });
            }
        },

        /**
         * Format file size
         * @param {number} bytes
         * @returns {string}
         */
        formatFileSize(bytes) {
            if (bytes === 0) return '0 Bytes';
            const k = 1024;
            const sizes = ['Bytes', 'KB', 'MB', 'GB'];
            const i = Math.floor(Math.log(bytes) / Math.log(k));
            return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
        },

        /**
         * Escape HTML to prevent XSS
         * @param {string} str
         * @returns {string}
         */
        escapeHtml(str) {
            if (!str) return '';
            const div = document.createElement('div');
            div.textContent = str;
            return div.innerHTML;
        },

        /**
         * Get form data as object
         * @param {string} formId
         * @returns {object}
         */
        getFormData(formId) {
            const form = document.getElementById(formId);
            if (!form) return {};

            const formData = new FormData(form);
            const data = {};

            formData.forEach((value, key) => {
                if (data[key]) {
                    if (!Array.isArray(data[key])) {
                        data[key] = [data[key]];
                    }
                    data[key].push(value);
                } else {
                    data[key] = value;
                }
            });

            return data;
        },

        /**
         * Set form field value
         * @param {string} fieldId
         * @param {*} value
         */
        setValue(fieldId, value) {
            const field = document.getElementById(fieldId);
            if (!field) return;

            if (field.type === 'checkbox') {
                field.checked = Boolean(value);
            } else {
                field.value = value || '';
            }
        },

        /**
         * Clear form errors
         * @param {string} formId
         */
        clearErrors(formId) {
            const form = document.getElementById(formId);
            if (!form) return;

            form.querySelectorAll('.error').forEach(el => {
                el.classList.remove('error', 'border-red-500');
                el.classList.add('border-gray-300');
            });

            form.querySelectorAll('[role="alert"]').forEach(el => {
                el.remove();
            });
        },

        /**
         * Display form errors
         * @param {object} errors - { fieldName: [errorMessages] }
         */
        showErrors(errors) {
            Object.entries(errors).forEach(([fieldName, messages]) => {
                const field = document.getElementById(fieldName);
                if (field) {
                    field.classList.add('error', 'border-red-500');
                    field.classList.remove('border-gray-300');

                    // Add error message
                    const existingError = field.parentElement.querySelector('[role="alert"]');
                    if (existingError) existingError.remove();

                    const errorEl = document.createElement('p');
                    errorEl.className = 'mt-1 text-xs text-red-500';
                    errorEl.setAttribute('role', 'alert');
                    errorEl.textContent = messages[0];
                    field.parentElement.appendChild(errorEl);
                }
            });
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = FormFieldComponent;
}

/**
 * Card Component
 * Section card with title, edit button, history button
 */

const CardComponent = (function() {
    return {
        /**
         * Render a card component
         * @param {object} options
         * @returns {string} HTML string
         */
        render(options) {
            const {
                id = '',
                title = '',
                subtitle = '',
                icon = '',
                content = '',
                editable = false,
                onEdit = '',
                showHistory = false,
                onHistory = '',
                effectiveDate = '',
                collapsed = false,
                collapsible = false,
                actions = [],
                className = ''
            } = options;

            const cardId = id || `card_${Date.now()}`;

            return `
                <div id="${cardId}" class="bg-white rounded-lg shadow-sm border border-gray-200 ${className}">
                    <div class="flex items-center justify-between px-4 py-3 border-b border-gray-100">
                        <div class="flex items-center gap-3">
                            ${icon ? `<span class="material-icons text-gray-500">${icon}</span>` : ''}
                            <div>
                                <h3 class="font-medium text-gray-900">${title}</h3>
                                ${subtitle ? `<p class="text-sm text-gray-500">${subtitle}</p>` : ''}
                            </div>
                        </div>
                        <div class="flex items-center gap-2">
                            ${effectiveDate ? `
                                <span class="text-xs text-gray-500">
                                    ${i18n.t('common.effectiveDate')}: ${DateUtils.format(effectiveDate, 'medium')}
                                </span>
                            ` : ''}
                            ${showHistory ? `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="${onHistory}"
                                    aria-label="${i18n.t('common.history')}"
                                    title="${i18n.t('common.history')}">
                                    <span class="material-icons text-gray-500 text-sm">history</span>
                                </button>
                            ` : ''}
                            ${editable ? `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="${onEdit}"
                                    aria-label="${i18n.t('common.edit')}"
                                    title="${i18n.t('common.edit')}">
                                    <span class="material-icons text-gray-500 text-sm">edit</span>
                                </button>
                            ` : ''}
                            ${actions.map(action => `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="${action.onclick || ''}"
                                    aria-label="${action.label}"
                                    title="${action.label}">
                                    <span class="material-icons text-gray-500 text-sm">${action.icon}</span>
                                </button>
                            `).join('')}
                            ${collapsible ? `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="CardComponent.toggle('${cardId}')"
                                    aria-expanded="${!collapsed}"
                                    aria-label="${collapsed ? i18n.t('accessibility.expandSection') : i18n.t('accessibility.collapseSection')}">
                                    <span class="material-icons text-gray-500 text-sm transition-transform ${collapsed ? '' : 'rotate-180'}" id="${cardId}-chevron">expand_more</span>
                                </button>
                            ` : ''}
                        </div>
                    </div>
                    <div id="${cardId}-content" class="p-4 ${collapsed ? 'hidden' : ''}">
                        ${content}
                    </div>
                </div>
            `;
        },

        /**
         * Render a data row (label-value pair)
         * @param {string} label
         * @param {string} value
         * @param {object} options
         * @returns {string}
         */
        dataRow(label, value, options = {}) {
            const { colspan = false, masked = false, icon = '' } = options;

            const displayValue = value || '-';

            return `
                <div class="${colspan ? 'col-span-2' : ''}">
                    <dt class="text-sm text-gray-500">${label}</dt>
                    <dd class="mt-1 text-sm text-gray-900 flex items-center gap-1">
                        ${icon ? `<span class="material-icons text-sm text-gray-400">${icon}</span>` : ''}
                        ${masked ? `<span class="font-mono">${displayValue}</span>` : displayValue}
                    </dd>
                </div>
            `;
        },

        /**
         * Render a data grid (multiple label-value pairs)
         * @param {array} items - Array of {label, value, options}
         * @param {number} columns - Number of columns (default 2)
         * @returns {string}
         */
        dataGrid(items, columns = 2) {
            return `
                <dl class="grid grid-cols-1 md:grid-cols-${columns} gap-4">
                    ${items.map(item => this.dataRow(item.label, item.value, item.options || {})).join('')}
                </dl>
            `;
        },

        /**
         * Toggle card collapse state
         * @param {string} cardId
         */
        toggle(cardId) {
            const content = document.getElementById(`${cardId}-content`);
            const chevron = document.getElementById(`${cardId}-chevron`);
            const button = chevron?.parentElement;

            if (content && chevron) {
                const isHidden = content.classList.contains('hidden');
                content.classList.toggle('hidden');
                chevron.classList.toggle('rotate-180');

                if (button) {
                    button.setAttribute('aria-expanded', isHidden);
                }
            }
        },

        /**
         * Render an empty state
         * @param {string} message
         * @param {string} icon
         * @returns {string}
         */
        emptyState(message, icon = 'inbox') {
            return `
                <div class="flex flex-col items-center justify-center py-8 text-gray-400">
                    <span class="material-icons text-4xl mb-2">${icon}</span>
                    <p class="text-sm">${message}</p>
                </div>
            `;
        },

        /**
         * Render a list item (for emergency contacts, dependents, etc.)
         * @param {object} options
         * @returns {string}
         */
        listItem(options) {
            const {
                id = '',
                title = '',
                subtitle = '',
                badge = '',
                badgeColor = 'bg-blue-100 text-blue-800',
                onEdit = '',
                onDelete = '',
                content = ''
            } = options;

            return `
                <div class="flex items-start justify-between p-4 border-b border-gray-100 last:border-b-0">
                    <div class="flex-1">
                        <div class="flex items-center gap-2">
                            <span class="font-medium text-gray-900">${title}</span>
                            ${badge ? `<span class="px-2 py-0.5 text-xs rounded-full ${badgeColor}">${badge}</span>` : ''}
                        </div>
                        ${subtitle ? `<p class="text-sm text-gray-500 mt-0.5">${subtitle}</p>` : ''}
                        ${content ? `<div class="mt-2">${content}</div>` : ''}
                    </div>
                    <div class="flex items-center gap-1 ml-4">
                        ${onEdit ? `
                            <button type="button"
                                class="p-1.5 hover:bg-gray-100 rounded-full transition"
                                onclick="${onEdit}"
                                aria-label="${i18n.t('common.edit')}">
                                <span class="material-icons text-gray-500 text-sm">edit</span>
                            </button>
                        ` : ''}
                        ${onDelete ? `
                            <button type="button"
                                class="p-1.5 hover:bg-red-50 rounded-full transition"
                                onclick="${onDelete}"
                                aria-label="${i18n.t('common.delete')}">
                                <span class="material-icons text-red-500 text-sm">delete</span>
                            </button>
                        ` : ''}
                    </div>
                </div>
            `;
        },

        /**
         * Render a "Show More" button
         * @param {string} onclick
         * @param {boolean} expanded
         * @returns {string}
         */
        showMoreButton(onclick, expanded = false) {
            return `
                <button type="button"
                    class="w-full py-2 text-sm text-cg-info hover:text-blue-700 flex items-center justify-center gap-1 transition"
                    onclick="${onclick}">
                    <span>${expanded ? i18n.t('common.showLess') : i18n.t('common.showMore')}</span>
                    <span class="material-icons text-sm ${expanded ? 'rotate-180' : ''}"">expand_more</span>
                </button>
            `;
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = CardComponent;
}

