   350→         * @param {string} tableId
   351→         * @param {array} data
   352→         */
   353→        updateData(tableId, data) {
   354→            const instance = tableInstances.get(tableId);
   355→            if (!instance) return;
   356→
   357→            instance.data = data;
   358→            instance.currentPage = 1;
   359→            instance.selectedRows.clear();
   360→
   361→            this.refresh(tableId);
   362→        },
   363→
   364→        /**
   365→         * Destroy table instance
   366→         * @param {string} tableId
   367→         */
   368→        destroy(tableId) {
   369→            tableInstances.delete(tableId);
   370→        }
   371→    };
   372→})();
