/**
 * Data Table Component
 * Sortable, paginated table for displaying lists
 */

const DataTableComponent = (function() {
    // Store table instances for sorting/pagination state
    const tableInstances = new Map();

    return {
        /**
         * Render a data table
         * @param {object} options
         * @returns {string}
         */
        render(options) {
            const {
                id,
                columns = [],
                data = [],
                sortable = true,
                paginated = true,
                pageSize = 10,
                emptyMessage = '',
                actions = [],
                selectable = false,
                className = ''
            } = options;

            const tableId = id || `table_${Date.now()}`;

            // Store instance data
            tableInstances.set(tableId, {
                columns,
                data,
                sortable,
                paginated,
                pageSize,
                currentPage: 1,
                sortColumn: null,
                sortDirection: 'asc',
                selectedRows: new Set()
            });

            if (data.length === 0) {
                return `
                    <div class="bg-white rounded-lg shadow-sm border border-gray-200 p-8 ${className}">
                        ${CardComponent.emptyState(emptyMessage || i18n.t('common.noData'))}
                    </div>
                `;
            }

            return `
                <div id="${tableId}" class="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden ${className}">
                    <div class="overflow-x-auto">
                        <table class="min-w-full divide-y divide-gray-200">
                            <thead class="bg-gray-50">
                                <tr>
                                    ${selectable ? `
                                        <th scope="col" class="px-4 py-3 w-10">
                                            <input type="checkbox"
                                                   class="w-4 h-4 text-cg-red border-gray-300 rounded"
                                                   onchange="DataTableComponent.toggleSelectAll('${tableId}', this.checked)">
                                        </th>
                                    ` : ''}
                                    ${columns.map(col => `
                                        <th scope="col"
                                            class="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider
                                                   ${sortable && col.sortable !== false ? 'cursor-pointer hover:bg-gray-100' : ''}"
                                            ${sortable && col.sortable !== false ? `onclick="DataTableComponent.sort('${tableId}', '${col.key}')"` : ''}>
                                            <div class="flex items-center gap-1">
                                                <span>${col.label}</span>
                                                ${sortable && col.sortable !== false ? `
                                                    <span class="material-icons text-sm text-gray-400" id="${tableId}-sort-${col.key}">unfold_more</span>
                                                ` : ''}
                                            </div>
                                        </th>
                                    `).join('')}
                                    ${actions.length > 0 ? `
                                        <th scope="col" class="px-4 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            ${i18n.t('common.actions')}
                                        </th>
                                    ` : ''}
                                </tr>
                            </thead>
                            <tbody id="${tableId}-body" class="bg-white divide-y divide-gray-200">
                                ${this.renderRows(tableId)}
                            </tbody>
                        </table>
                    </div>
                    ${paginated && data.length > pageSize ? this.renderPagination(tableId) : ''}
                </div>
            `;
        },

        /**
         * Render table rows
         * @param {string} tableId
         * @returns {string}
         */
        renderRows(tableId) {
            const instance = tableInstances.get(tableId);
            if (!instance) return '';

            let { data, columns, pageSize, currentPage, sortColumn, sortDirection, selectedRows } = instance;

            // Sort data if needed
            if (sortColumn) {
                data = [...data].sort((a, b) => {
                    let aVal = a[sortColumn];
                    let bVal = b[sortColumn];

                    // Handle null/undefined
                    if (aVal == null) aVal = '';
                    if (bVal == null) bVal = '';

                    // String comparison
                    if (typeof aVal === 'string') {
                        return sortDirection === 'asc'
                            ? aVal.localeCompare(bVal)
                            : bVal.localeCompare(aVal);
                    }

                    // Number comparison
                    return sortDirection === 'asc' ? aVal - bVal : bVal - aVal;
                });
            }

            // Paginate
            const start = (currentPage - 1) * pageSize;
            const pageData = data.slice(start, start + pageSize);

            return pageData.map((row, index) => {
                const rowId = row.id || `row_${start + index}`;
                const isSelected = selectedRows.has(rowId);

                return `
                    <tr class="${isSelected ? 'bg-blue-50' : 'hover:bg-gray-50'} transition">
                        ${instance.selectable ? `
                            <td class="px-4 py-3 w-10">
                                <input type="checkbox"
                                       class="w-4 h-4 text-cg-red border-gray-300 rounded"
                                       ${isSelected ? 'checked' : ''}
                                       onchange="DataTableComponent.toggleSelect('${tableId}', '${rowId}', this.checked)">
                            </td>
                        ` : ''}
                        ${columns.map(col => {
                            let value = row[col.key];

                            // Apply formatter if provided
                            if (col.format) {
                                value = col.format(value, row);
                            }

                            // Apply renderer if provided
                            if (col.render) {
                                return `<td class="px-4 py-3 ${col.className || ''}">${col.render(value, row)}</td>`;
                            }

                            return `<td class="px-4 py-3 text-sm text-gray-900 ${col.className || ''}">${value ?? '-'}</td>`;
                        }).join('')}
                        ${instance.actions?.length > 0 ? `
                            <td class="px-4 py-3 text-right">
                                <div class="flex items-center justify-end gap-1">
                                    ${instance.actions.map(action => `
                                        <button type="button"
                                                class="p-1.5 hover:bg-gray-100 rounded transition"
                                                onclick="${action.onclick.replace('{id}', rowId).replace('{row}', JSON.stringify(row))}"
                                                title="${action.label}">
                                            <span class="material-icons text-sm ${action.iconClass || 'text-gray-500'}">${action.icon}</span>
                                        </button>
                                    `).join('')}
                                </div>
                            </td>
                        ` : ''}
                    </tr>
                `;
            }).join('');
        },

        /**
         * Render pagination controls
         * @param {string} tableId
         * @returns {string}
         */
        renderPagination(tableId) {
            const instance = tableInstances.get(tableId);
            if (!instance) return '';

            const { data, pageSize, currentPage } = instance;
            const totalPages = Math.ceil(data.length / pageSize);
            const start = (currentPage - 1) * pageSize + 1;
            const end = Math.min(currentPage * pageSize, data.length);

            return `
                <div class="flex items-center justify-between px-4 py-3 border-t border-gray-200 bg-gray-50">
                    <div class="text-sm text-gray-500">
                        ${i18n.t('common.showing')} ${start} - ${end} ${i18n.t('common.of')} ${data.length} ${i18n.t('common.items')}
                    </div>
                    <div class="flex items-center gap-2">
                        <button type="button"
                                class="p-1.5 rounded hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                                ${currentPage === 1 ? 'disabled' : ''}
                                onclick="DataTableComponent.goToPage('${tableId}', ${currentPage - 1})">
                            <span class="material-icons text-sm">chevron_left</span>
                        </button>
                        <span class="text-sm text-gray-600">${currentPage} / ${totalPages}</span>
                        <button type="button"
                                class="p-1.5 rounded hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                                ${currentPage === totalPages ? 'disabled' : ''}
                                onclick="DataTableComponent.goToPage('${tableId}', ${currentPage + 1})">
                            <span class="material-icons text-sm">chevron_right</span>
                        </button>
                    </div>
                </div>
            `;
        },

        /**
         * Sort table by column
         * @param {string} tableId
         * @param {string} column
         */
        sort(tableId, column) {
            const instance = tableInstances.get(tableId);
            if (!instance) return;

            // Toggle sort direction if same column
            if (instance.sortColumn === column) {
                instance.sortDirection = instance.sortDirection === 'asc' ? 'desc' : 'asc';
            } else {
                instance.sortColumn = column;
                instance.sortDirection = 'asc';
            }

            // Update sort icons
            instance.columns.forEach(col => {
                const icon = document.getElementById(`${tableId}-sort-${col.key}`);
                if (icon) {
                    if (col.key === column) {
                        icon.textContent = instance.sortDirection === 'asc' ? 'arrow_upward' : 'arrow_downward';
                        icon.classList.remove('text-gray-400');
                        icon.classList.add('text-cg-red');
                    } else {
                        icon.textContent = 'unfold_more';
                        icon.classList.add('text-gray-400');
                        icon.classList.remove('text-cg-red');
                    }
                }
            });

            // Re-render rows
            this.refresh(tableId);
        },

        /**
         * Go to specific page
         * @param {string} tableId
         * @param {number} page
         */
        goToPage(tableId, page) {
            const instance = tableInstances.get(tableId);
            if (!instance) return;

            const totalPages = Math.ceil(instance.data.length / instance.pageSize);
            instance.currentPage = Math.max(1, Math.min(page, totalPages));

            this.refresh(tableId);
        },

        /**
         * Toggle row selection
         * @param {string} tableId
         * @param {string} rowId
         * @param {boolean} selected
         */
        toggleSelect(tableId, rowId, selected) {
            const instance = tableInstances.get(tableId);
            if (!instance) return;

            if (selected) {
                instance.selectedRows.add(rowId);
            } else {
                instance.selectedRows.delete(rowId);
            }

            this.refresh(tableId);
        },

        /**
         * Toggle select all
         * @param {string} tableId
         * @param {boolean} selected
         */
        toggleSelectAll(tableId, selected) {
            const instance = tableInstances.get(tableId);
            if (!instance) return;

            if (selected) {
                instance.data.forEach((row, index) => {
                    instance.selectedRows.add(row.id || `row_${index}`);
                });
            } else {
                instance.selectedRows.clear();
            }

            this.refresh(tableId);
        },

        /**
         * Get selected rows
         * @param {string} tableId
         * @returns {array}
         */
        getSelected(tableId) {
            const instance = tableInstances.get(tableId);
            if (!instance) return [];

            return instance.data.filter((row, index) => {
                const rowId = row.id || `row_${index}`;
                return instance.selectedRows.has(rowId);
            });
        },

        /**
         * Refresh table display
         * @param {string} tableId
         */
        refresh(tableId) {
            const instance = tableInstances.get(tableId);
            if (!instance) return;

            const tbody = document.getElementById(`${tableId}-body`);
            if (tbody) {
                tbody.innerHTML = this.renderRows(tableId);
            }

            // Update pagination
            const table = document.getElementById(tableId);
            if (table && instance.paginated) {
                const pagination = table.querySelector('.border-t.border-gray-200');
                if (pagination) {
                    pagination.outerHTML = this.renderPagination(tableId);
                }
            }
        },

        /**
         * Update table data
         * @param {string} tableId
         * @param {array} data
         */
        updateData(tableId, data) {
            const instance = tableInstances.get(tableId);
            if (!instance) return;

            instance.data = data;
            instance.currentPage = 1;
            instance.selectedRows.clear();

            this.refresh(tableId);
        },

        /**
         * Destroy table instance
         * @param {string} tableId
         */
        destroy(tableId) {
            tableInstances.delete(tableId);
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = DataTableComponent;
}

/**
 * Header Component
 * Main navigation header with logo, search, icons
 */

const HeaderComponent = (function() {
    return {
        /**
         * Render the header
         * @returns {string}
         */
        render() {
            const user = AppState.get('currentUser');
            const unreadCount = AppState.getUnreadCount();

            return `
                <header class="bg-white shadow-md sticky top-0 z-40">
                    <div class="flex items-center justify-between px-4 py-2">
                        <!-- Logo and Menu -->
                        <div class="flex items-center gap-4">
                            <!-- Mobile Menu Button -->
                            <button class="lg:hidden p-2 hover:bg-gray-100 rounded-lg min-h-[44px] min-w-[44px]"
                                    onclick="MobileMenuComponent.toggle()"
                                    aria-label="${i18n.t('accessibility.openMenu')}"
                                    aria-expanded="false"
                                    id="mobile-menu-btn">
                                <span class="material-icons">menu</span>
                            </button>

                            <!-- Logo -->
                            <a href="#/home" class="flex items-center gap-2">
                                <div class="text-xl font-bold">
                                    <span class="text-cg-red">CENTRAL</span><span class="text-gray-800">GROUP</span>
                                </div>
                            </a>

                            <!-- Employee Files Dropdown -->
                            <div class="relative hidden md:block">
                                <button class="flex items-center gap-1 px-3 py-2 hover:bg-gray-100 rounded-lg transition"
                                        onclick="HeaderComponent.toggleDropdown('nav-dropdown')">
                                    <span class="text-gray-700">${i18n.t('nav.employeeFiles')}</span>
                                    <span class="material-icons text-sm text-gray-500">expand_more</span>
                                </button>
                                <div id="nav-dropdown" class="dropdown-menu absolute left-0 top-full mt-1 w-48 bg-white rounded-lg shadow-lg border py-1">
                                    <a href="#/home" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">home</span>
                                        <span>${i18n.t('nav.home')}</span>
                                    </a>
                                    <a href="#/profile" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">person</span>
                                        <span>${i18n.t('nav.profile')}</span>
                                    </a>
                                    <a href="#/workflows" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">assignment</span>
                                        <span>${i18n.t('nav.workflows')}</span>
                                    </a>
                                </div>
                            </div>
                        </div>

                        <!-- Search Bar -->
                        <div class="flex-1 max-w-xl mx-4 hidden md:block">
                            <div class="relative">
                                <span class="material-icons absolute left-3 top-2.5 text-gray-400">search</span>
                                <input type="text"
                                       placeholder="${i18n.t('nav.searchPlaceholder')}"
                                       class="w-full pl-10 pr-4 py-2 border rounded-full bg-gray-50 focus:outline-none focus:ring-2 focus:ring-cg-info focus:bg-white transition"
                                       aria-label="${i18n.t('common.search')}"
                                       onkeydown="if(event.key==='Enter') HeaderComponent.handleSearch(this.value)">
                            </div>
                        </div>

                        <!-- Action Icons -->
                        <div class="flex items-center gap-1">
                            <!-- Search Icon (Mobile) -->
                            <button class="md:hidden p-2 hover:bg-gray-100 rounded-full"
                                    onclick="HeaderComponent.toggleMobileSearch()">
                                <span class="material-icons text-gray-600">search</span>
                            </button>

                            <!-- Notifications -->
                            ${NotificationBellComponent.render(unreadCount)}

                            <!-- Help -->
                            <button class="hidden sm:block p-2 hover:bg-gray-100 rounded-full" title="${i18n.t('nav.help')}">
                                <span class="material-icons text-gray-600">help_outline</span>
                            </button>

                            <!-- Settings -->
                            <button class="hidden sm:block p-2 hover:bg-gray-100 rounded-full" title="${i18n.t('nav.settings')}">
                                <span class="material-icons text-gray-600">settings</span>
                            </button>

                            <!-- Language Toggle -->
                            <button class="p-2 hover:bg-gray-100 rounded-full"
                                    onclick="HeaderComponent.toggleLanguage()"
                                    title="${i18n.isThai() ? 'Switch to English' : 'เปลี่ยนเป็นภาษาไทย'}">
                                <span class="text-sm font-medium text-gray-600">${i18n.isThai() ? 'EN' : 'TH'}</span>
                            </button>

                            <!-- User Avatar -->
                            <div class="relative ml-2">
                                <button class="flex items-center gap-2 p-1 hover:bg-gray-100 rounded-full"
                                        onclick="HeaderComponent.toggleDropdown('user-dropdown')">
                                    <div class="w-8 h-8 bg-cg-red rounded-full flex items-center justify-center text-white font-medium text-sm">
                                        ${user ? (user.username || 'U').substring(0, 2).toUpperCase() : 'U'}
                                    </div>
                                    <span class="material-icons text-sm text-gray-500 hidden sm:block">expand_more</span>
                                </button>
                                <div id="user-dropdown" class="dropdown-menu absolute right-0 top-full mt-1 w-48 bg-white rounded-lg shadow-lg border py-1">
                                    <div class="px-4 py-2 border-b">
                                        <p class="font-medium text-gray-900">${user?.username || 'User'}</p>
                                        <p class="text-sm text-gray-500">${RBAC.getRoleDisplayName(user?.role)}</p>
                                    </div>
                                    <a href="#/profile" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">person</span>
                                        <span>${i18n.t('nav.profile')}</span>
                                    </a>
                                    <a href="#/settings" class="flex items-center gap-2 px-4 py-2 hover:bg-gray-50">
                                        <span class="material-icons text-sm text-gray-500">settings</span>
                                        <span>${i18n.t('nav.settings')}</span>
                                    </a>
                                    <hr class="my-1">
                                    <button class="w-full flex items-center gap-2 px-4 py-2 hover:bg-gray-50 text-red-600"
                                            onclick="HeaderComponent.logout()">
                                        <span class="material-icons text-sm">logout</span>
                                        <span>${i18n.t('nav.logout')}</span>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                    <!-- Mobile Search (Hidden by default) -->
                    <div id="mobile-search" class="hidden px-4 pb-3 md:hidden">
                        <div class="relative">
                            <span class="material-icons absolute left-3 top-2.5 text-gray-400">search</span>
                            <input type="text"
                                   placeholder="${i18n.t('nav.searchPlaceholder')}"
                                   class="w-full pl-10 pr-4 py-2 border rounded-full bg-gray-50 focus:outline-none focus:ring-2 focus:ring-cg-info"
                                   onkeydown="if(event.key==='Enter') HeaderComponent.handleSearch(this.value)">
                        </div>
                    </div>
                </header>
            `;
        },

        /**
         * Initialize header event listeners
         */
        init() {
            // Subscribe to notification changes
            AppState.subscribe('notifications', () => {
                this.updateNotificationCount();
            });
        },

        /**
         * Toggle dropdown visibility
         * @param {string} id
         */
        toggleDropdown(id) {
            const dropdown = document.getElementById(id);
            if (dropdown) {
                dropdown.classList.toggle('open');
            }
        },

        /**
         * Toggle mobile search
         */
        toggleMobileSearch() {
            const search = document.getElementById('mobile-search');
            if (search) {
                search.classList.toggle('hidden');
                if (!search.classList.contains('hidden')) {
                    search.querySelector('input').focus();
                }
            }
        },

        /**
         * Handle search
         * @param {string} query
         */
        handleSearch(query) {
            if (query.trim()) {
                console.log('Search:', query);
                // Implement search functionality
                ToastComponent.info(`Searching for: ${query}`);
            }
        },

        /**
         * Toggle language
         */
        toggleLanguage() {
            i18n.toggleLanguage();
            ToastComponent.success(i18n.isThai() ? 'เปลี่ยนเป็นภาษาไทยแล้ว' : 'Switched to English');
        },

        /**
         * Update notification count in header
         */
        updateNotificationCount() {
            const count = AppState.getUnreadCount();
            const badge = document.getElementById('notification-badge');
            if (badge) {
                badge.textContent = count;
                badge.classList.toggle('hidden', count === 0);
            }
        },

        /**
         * Logout handler
         */
        logout() {
            ModalComponent.confirm({
                title: i18n.t('nav.logout'),
                message: i18n.isThai() ? 'คุณต้องการออกจากระบบหรือไม่?' : 'Are you sure you want to logout?',
                confirmText: i18n.t('nav.logout'),
                icon: 'logout'
            }).then(confirmed => {
                if (confirmed) {
                    // Clear state and redirect
                    AppState.reset();
                    ToastComponent.success(i18n.isThai() ? 'ออกจากระบบแล้ว' : 'Logged out successfully');
                    // In real app, redirect to login page
                    Router.navigate('home');
                }
            });
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = HeaderComponent;
}

