   307→        handleFileSelect(input, previewId) {
   308→            const preview = document.getElementById(previewId);
   309→            if (!preview) return;
   310→
   311→            preview.innerHTML = '';
   312→
   313→            if (input.files) {
   314→                Array.from(input.files).forEach(file => {
   315→                    const fileItem = document.createElement('div');
   316→                    fileItem.className = 'flex items-center gap-2 p-2 bg-gray-50 rounded';
   317→                    fileItem.innerHTML = `
