/**
 * API Module
 * Mock API client with simulated delays for realistic UX
 */

const API = (function() {
    // Simulated network delay (ms)
    const NETWORK_DELAY = 300;

    // Simulate network delay
    function delay(ms = NETWORK_DELAY) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }

    // Generate random ID
    function generateId() {
        return Math.random().toString(36).substring(2, 15);
    }

    // Deep clone utility
    function clone(obj) {
        return JSON.parse(JSON.stringify(obj));
    }

    return {
        /**
         * Get employee by ID
         * @param {string} employeeId
         * @returns {Promise<object>}
         */
        async getEmployee(employeeId) {
            await delay();
            const employee = clone(MockEmployeeData.employee);
            if (!employee) {
                throw new Error('Employee not found');
            }
            return employee;
        },

        /**
         * Get current logged-in user
         * @returns {Promise<object>}
         */
        async getCurrentUser() {
            await delay(100);
            return clone(MockEmployeeData.currentUser);
        },

        /**
         * Update employee personal info
         * @param {string} employeeId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async updatePersonalInfo(employeeId, data) {
            await delay(500);
            // Merge with existing data
            Object.assign(MockEmployeeData.employee.personalInfo, data);
            return { success: true, data: clone(MockEmployeeData.employee.personalInfo) };
        },

        /**
         * Update contact information (self-service)
         * @param {string} employeeId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async updateContactInfo(employeeId, data) {
            await delay(400);
            Object.assign(MockEmployeeData.employee.contactInfo, data);
            return { success: true, data: clone(MockEmployeeData.employee.contactInfo) };
        },

        /**
         * Update address information
         * @param {string} employeeId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async updateAddress(employeeId, data) {
            await delay(500);
            const addresses = MockEmployeeData.employee.addresses;
            const index = addresses.findIndex(a => a.addressType === data.addressType);
            if (index !== -1) {
                addresses[index] = { ...addresses[index], ...data };
            } else {
                addresses.push(data);
            }
            return { success: true, data: clone(addresses) };
        },

        /**
         * Get emergency contacts
         * @param {string} employeeId
         * @returns {Promise<array>}
         */
        async getEmergencyContacts(employeeId) {
            await delay();
            return clone(MockEmployeeData.employee.emergencyContacts || []);
        },

        /**
         * Add emergency contact (self-service)
         * @param {string} employeeId
         * @param {object} contact
         * @returns {Promise<object>}
         */
        async addEmergencyContact(employeeId, contact) {
            await delay(400);
            const newContact = {
                id: generateId(),
                ...contact
            };
            MockEmployeeData.employee.emergencyContacts.push(newContact);
            return { success: true, data: newContact };
        },

        /**
         * Update emergency contact
         * @param {string} employeeId
         * @param {string} contactId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async updateEmergencyContact(employeeId, contactId, data) {
            await delay(400);
            const contacts = MockEmployeeData.employee.emergencyContacts;
            const index = contacts.findIndex(c => c.id === contactId);
            if (index !== -1) {
                contacts[index] = { ...contacts[index], ...data };
                return { success: true, data: clone(contacts[index]) };
            }
            throw new Error('Contact not found');
        },

        /**
         * Delete emergency contact
         * @param {string} employeeId
         * @param {string} contactId
         * @returns {Promise<object>}
         */
        async deleteEmergencyContact(employeeId, contactId) {
            await delay(300);
            const contacts = MockEmployeeData.employee.emergencyContacts;
            const index = contacts.findIndex(c => c.id === contactId);
            if (index !== -1) {
                contacts.splice(index, 1);
                return { success: true };
            }
            throw new Error('Contact not found');
        },

        /**
         * Get dependents
         * @param {string} employeeId
         * @returns {Promise<array>}
         */
        async getDependents(employeeId) {
            await delay();
            return clone(MockEmployeeData.employee.dependents || []);
        },

        /**
         * Add dependent
         * @param {string} employeeId
         * @param {object} dependent
         * @returns {Promise<object>}
         */
        async addDependent(employeeId, dependent) {
            await delay(400);
            const newDependent = {
                id: generateId(),
                ...dependent
            };
            MockEmployeeData.employee.dependents.push(newDependent);
            return { success: true, data: newDependent };
        },

        /**
         * Update dependent
         * @param {string} employeeId
         * @param {string} dependentId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async updateDependent(employeeId, dependentId, data) {
            await delay(400);
            const dependents = MockEmployeeData.employee.dependents;
            const index = dependents.findIndex(d => d.id === dependentId);
            if (index !== -1) {
                dependents[index] = { ...dependents[index], ...data };
                return { success: true, data: clone(dependents[index]) };
            }
            throw new Error('Dependent not found');
        },

        /**
         * Delete dependent
         * @param {string} employeeId
         * @param {string} dependentId
         * @returns {Promise<object>}
         */
        async deleteDependent(employeeId, dependentId) {
            await delay(300);
            const dependents = MockEmployeeData.employee.dependents;
            const index = dependents.findIndex(d => d.id === dependentId);
            if (index !== -1) {
                dependents.splice(index, 1);
                return { success: true };
            }
            throw new Error('Dependent not found');
        },

        /**
         * Get employment information
         * @param {string} employeeId
         * @returns {Promise<object>}
         */
        async getEmploymentInfo(employeeId) {
            await delay();
            return clone(MockEmployeeData.employee.employmentInfo);
        },

        /**
         * Get compensation information
         * @param {string} employeeId
         * @returns {Promise<object>}
         */
        async getCompensation(employeeId) {
            await delay();
            return clone(MockEmployeeData.employee.compensation);
        },

        /**
         * Get benefits
         * @param {string} employeeId
         * @returns {Promise<object>}
         */
        async getBenefits(employeeId) {
            await delay();
            return clone(MockEmployeeData.employee.benefits);
        },

        /**
         * Get organization chart
         * @param {string} employeeId
         * @returns {Promise<object>}
         */
        async getOrgChart(employeeId) {
            await delay();
            return clone(MockEmployeeData.employee.orgChart);
        },

        /**
         * Get payslips list
         * @param {string} employeeId
         * @param {object} options
         * @returns {Promise<array>}
         */
        async getPayslips(employeeId, options = {}) {
            await delay();
            return clone(MockEmployeeData.employee.payslips || []);
        },

        /**
         * Download payslip
         * @param {string} employeeId
         * @param {string} payslipId
         * @returns {Promise<Blob>}
         */
        async downloadPayslip(employeeId, payslipId) {
            await delay(1000);
            // Return mock blob
            return new Blob(['Mock payslip content'], { type: 'application/pdf' });
        },

        /**
         * Download E-Letter
         * @param {string} employeeId
         * @param {string} letterId
         * @returns {Promise<Blob>}
         */
        async downloadELetter(employeeId, letterId) {
            await delay(1000);
            return new Blob(['Mock E-Letter content'], { type: 'application/pdf' });
        },

        /**
         * Download Certificate
         * @param {string} employeeId
         * @param {string} courseId
         * @returns {Promise<Blob>}
         */
        async downloadCertificate(employeeId, courseId) {
            await delay(1000);
            return new Blob(['Mock certificate content'], { type: 'application/pdf' });
        },

        /**
         * Download OHS Document
         * @param {string} employeeId
         * @param {string} docId
         * @returns {Promise<Blob>}
         */
        async downloadOHSDocument(employeeId, docId) {
            await delay(1000);
            return new Blob(['Mock OHS document content'], { type: 'application/pdf' });
        },

        /**
         * Download Individual Document
         * @param {string} employeeId
         * @param {string} docId
         * @returns {Promise<Blob>}
         */
        async downloadIndividualDocument(employeeId, docId) {
            await delay(1000);
            return new Blob(['Mock individual document content'], { type: 'application/pdf' });
        },

        /**
         * Download Tax Document
         * @param {string} employeeId - The employee ID
         * @param {string} taxDocId - The tax document ID
         * @returns {Promise<Blob>}
         */
        async downloadTaxDocument(employeeId, taxDocId) {
            await delay(1000);
            return new Blob(['Mock tax document content'], { type: 'application/pdf' });
        },

        /**
         * Get change history
         * @param {string} employeeId
         * @param {string} section
         * @param {object} options
         * @returns {Promise<array>}
         */
        async getHistory(employeeId, section, options = {}) {
            await delay();
            return clone(MockEmployeeData.employee.history?.[section] || []);
        },

        /**
         * Submit workflow request
         * @param {object} request
         * @returns {Promise<object>}
         */
        async submitWorkflowRequest(request) {
            await delay(500);
            const newRequest = {
                id: generateId(),
                status: 'pending',
                submittedAt: new Date().toISOString(),
                ...request
            };
            MockWorkflowData.requests.push(newRequest);
            return { success: true, data: newRequest };
        },

        /**
         * Get pending workflows
         * @param {string} userId
         * @param {string} type - 'forApproval' | 'sentBack' | 'approved' | 'reassigned'
         * @returns {Promise<array>}
         */
        async getPendingWorkflows(userId, type = 'forApproval') {
            await delay();
            return clone(MockWorkflowData[type] || []);
        },

        /**
         * Approve workflow request
         * @param {string} requestId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async approveWorkflow(requestId, data = {}) {
            await delay(500);
            const request = MockWorkflowData.requests.find(r => r.id === requestId);
            if (request) {
                request.status = 'approved';
                request.approvedAt = new Date().toISOString();
                request.approverComments = data.comments;
                return { success: true, data: clone(request) };
            }
            throw new Error('Request not found');
        },

        /**
         * Reject workflow request
         * @param {string} requestId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async rejectWorkflow(requestId, data = {}) {
            await delay(500);
            const request = MockWorkflowData.requests.find(r => r.id === requestId);
            if (request) {
                request.status = 'rejected';
                request.rejectedAt = new Date().toISOString();
                request.rejectionReason = data.reason;
                return { success: true, data: clone(request) };
            }
            throw new Error('Request not found');
        },

        /**
         * Send back workflow request
         * @param {string} requestId
         * @param {object} data
         * @returns {Promise<object>}
         */
        async sendBackWorkflow(requestId, data = {}) {
            await delay(500);
            const request = MockWorkflowData.requests.find(r => r.id === requestId);
            if (request) {
                request.status = 'sentBack';
                request.sentBackAt = new Date().toISOString();
                request.sendBackReason = data.reason;
                return { success: true, data: clone(request) };
            }
            throw new Error('Request not found');
        },

        /**
         * Get notifications
         * @param {string} userId
         * @returns {Promise<array>}
         */
        async getNotifications(userId) {
            await delay(200);
            return clone(MockWorkflowData.notifications || []);
        },

        /**
         * Mark notification as read
         * @param {string} notificationId
         * @returns {Promise<object>}
         */
        async markNotificationRead(notificationId) {
            await delay(100);
            const notification = MockWorkflowData.notifications.find(n => n.id === notificationId);
            if (notification) {
                notification.read = true;
                return { success: true };
            }
            throw new Error('Notification not found');
        },

        /**
         * Get lookup data
         * @param {string} type
         * @returns {Promise<array>}
         */
        async getLookup(type) {
            await delay(100);
            return clone(MockLookupData[type] || []);
        },

        /**
         * Search employees
         * @param {string} query
         * @returns {Promise<array>}
         */
        async searchEmployees(query) {
            await delay(300);
            // Return mock search results
            return [clone(MockEmployeeData.employee)];
        },

        /**
         * Get team members (for managers)
         * @param {string} managerId
         * @returns {Promise<array>}
         */
        async getTeamMembers(managerId) {
            await delay();
            return clone(MockEmployeeData.teamMembers || []);
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = API;
}

/**
 * Home Page
 * Quick Actions and dashboard
 */

const HomePage = (function() {
    return {
        /**
         * Render home page
         * @returns {string}
         */
        render() {
            const user = AppState.get('currentUser');
            const employee = AppState.get('currentEmployee');
            const pendingWorkflows = AppState.get('pendingWorkflows') || [];
            const isManager = RBAC.isManager();

            const isThai = i18n.isThai();
            const greeting = this.getGreeting();
            const name = isThai
                ? (employee?.personalInfo?.firstNameTh || employee?.personalInfo?.firstNameEn || user?.username)
                : (employee?.personalInfo?.firstNameEn || user?.username);

            return `
                <div class="max-w-7xl mx-auto px-4 sm:px-6 py-6">
                    <!-- Welcome Message -->
                    <div class="mb-8">
                        <h1 class="text-2xl sm:text-3xl font-bold text-gray-900">
                            ${greeting}, ${name}!
                        </h1>
                        <p class="text-gray-500 mt-1">
                            ${isThai ? 'ยินดีต้อนรับสู่ระบบ HR' : 'Welcome to the HR System'}
                        </p>
                    </div>

                    <!-- Quick Actions -->
                    <section class="mb-8">
                        <h2 class="text-lg font-semibold text-gray-900 mb-4">${i18n.t('home.quickActions')}</h2>
                        <div class="grid grid-cols-1 md:grid-cols-3 gap-4">
                            ${this.renderQuickActionCard({
                                icon: 'person',
                                iconBg: 'bg-blue-100',
                                iconColor: 'text-blue-600',
                                title: i18n.t('home.viewMyProfile'),
                                description: i18n.t('home.viewMyProfileDesc'),
                                href: '#/profile'
                            })}
                            ${this.renderQuickActionCard({
                                icon: 'edit_note',
                                iconBg: 'bg-green-100',
                                iconColor: 'text-green-600',
                                title: i18n.t('home.manageMyData'),
                                description: i18n.t('home.manageMyDataDesc'),
                                href: '#/profile/personal'
                            })}
                            ${this.renderQuickActionCard({
                                icon: 'assignment',
                                iconBg: 'bg-orange-100',
                                iconColor: 'text-orange-600',
                                title: i18n.t('home.viewPendingWorkflows'),
                                description: i18n.t('home.viewPendingWorkflowsDesc'),
                                href: '#/workflows',
                                badge: pendingWorkflows.length > 0 ? pendingWorkflows.length : null
                            })}
                        </div>
                    </section>

                    <div class="grid grid-cols-1 lg:grid-cols-2 gap-6">
                        <!-- For You Today -->
                        <section>
                            <h2 class="text-lg font-semibold text-gray-900 mb-4">${i18n.t('home.forYouToday')}</h2>
                            <div class="bg-white rounded-lg shadow-sm border border-gray-200">
                                ${this.renderForYouToday()}
                            </div>
                        </section>

                        <!-- Team Summary (for managers) -->
                        ${isManager ? `
                            <section>
                                <h2 class="text-lg font-semibold text-gray-900 mb-4">${i18n.t('home.teamSummary')}</h2>
                                <div class="bg-white rounded-lg shadow-sm border border-gray-200">
                                    ${this.renderTeamSummary()}
                                </div>
                            </section>
                        ` : ''}
                    </div>
                </div>
            `;
        },

        /**
         * Initialize home page
         */
        init() {
            // Any initialization logic
        },

        /**
         * Get greeting based on time of day
         * @returns {string}

/**
 * Card Component
 * Section card with title, edit button, history button
 */

const CardComponent = (function() {
    return {
        /**
         * Render a card component
         * @param {object} options
         * @returns {string} HTML string
         */
        render(options) {
            const {
                id = '',
                title = '',
                subtitle = '',
                icon = '',
                content = '',
                editable = false,
                onEdit = '',
                showHistory = false,
                onHistory = '',
                effectiveDate = '',
                collapsed = false,
                collapsible = false,
                actions = [],
                className = ''
            } = options;

            const cardId = id || `card_${Date.now()}`;

            return `
                <div id="${cardId}" class="bg-white rounded-lg shadow-sm border border-gray-200 ${className}">
                    <div class="flex items-center justify-between px-4 py-3 border-b border-gray-100">
                        <div class="flex items-center gap-3">
                            ${icon ? `<span class="material-icons text-gray-500">${icon}</span>` : ''}
                            <div>
                                <h3 class="font-medium text-gray-900">${title}</h3>
                                ${subtitle ? `<p class="text-sm text-gray-500">${subtitle}</p>` : ''}
                            </div>
                        </div>
                        <div class="flex items-center gap-2">
                            ${effectiveDate ? `
                                <span class="text-xs text-gray-500">
                                    ${i18n.t('common.effectiveDate')}: ${DateUtils.format(effectiveDate, 'medium')}
                                </span>
                            ` : ''}
                            ${showHistory ? `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="${onHistory}"
                                    aria-label="${i18n.t('common.history')}"
                                    title="${i18n.t('common.history')}">
                                    <span class="material-icons text-gray-500 text-sm">history</span>
                                </button>
                            ` : ''}
                            ${editable ? `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="${onEdit}"
                                    aria-label="${i18n.t('common.edit')}"
                                    title="${i18n.t('common.edit')}">
                                    <span class="material-icons text-gray-500 text-sm">edit</span>
                                </button>
                            ` : ''}
                            ${actions.map(action => `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="${action.onclick || ''}"
                                    aria-label="${action.label}"
                                    title="${action.label}">
                                    <span class="material-icons text-gray-500 text-sm">${action.icon}</span>
                                </button>
                            `).join('')}
                            ${collapsible ? `
                                <button type="button"
                                    class="p-2 hover:bg-gray-100 rounded-full transition"
                                    onclick="CardComponent.toggle('${cardId}')"
                                    aria-expanded="${!collapsed}"
                                    aria-label="${collapsed ? i18n.t('accessibility.expandSection') : i18n.t('accessibility.collapseSection')}">
                                    <span class="material-icons text-gray-500 text-sm transition-transform ${collapsed ? '' : 'rotate-180'}" id="${cardId}-chevron">expand_more</span>
                                </button>
                            ` : ''}
                        </div>
                    </div>
                    <div id="${cardId}-content" class="p-4 ${collapsed ? 'hidden' : ''}">
                        ${content}
                    </div>
                </div>
            `;
        },

        /**
         * Render a data row (label-value pair)
         * @param {string} label
         * @param {string} value
         * @param {object} options
         * @returns {string}
         */
        dataRow(label, value, options = {}) {
            const { colspan = false, masked = false, icon = '' } = options;

            const displayValue = value || '-';

            return `
                <div class="${colspan ? 'col-span-2' : ''}">
                    <dt class="text-sm text-gray-500">${label}</dt>
                    <dd class="mt-1 text-sm text-gray-900 flex items-center gap-1">
                        ${icon ? `<span class="material-icons text-sm text-gray-400">${icon}</span>` : ''}
                        ${masked ? `<span class="font-mono">${displayValue}</span>` : displayValue}
                    </dd>
                </div>
            `;
        },

        /**
         * Render a data grid (multiple label-value pairs)
         * @param {array} items - Array of {label, value, options}
         * @param {number} columns - Number of columns (default 2)
         * @returns {string}
         */
        dataGrid(items, columns = 2) {
            return `
                <dl class="grid grid-cols-1 md:grid-cols-${columns} gap-4">
                    ${items.map(item => this.dataRow(item.label, item.value, item.options || {})).join('')}
                </dl>
            `;
        },

        /**
         * Toggle card collapse state
         * @param {string} cardId
         */
        toggle(cardId) {
            const content = document.getElementById(`${cardId}-content`);
            const chevron = document.getElementById(`${cardId}-chevron`);
            const button = chevron?.parentElement;

            if (content && chevron) {
                const isHidden = content.classList.contains('hidden');
                content.classList.toggle('hidden');
                chevron.classList.toggle('rotate-180');

                if (button) {
                    button.setAttribute('aria-expanded', isHidden);
                }
            }
        },

        /**
         * Render an empty state
         * @param {string} message
         * @param {string} icon
         * @returns {string}
         */
        emptyState(message, icon = 'inbox') {
            return `
                <div class="flex flex-col items-center justify-center py-8 text-gray-400">
                    <span class="material-icons text-4xl mb-2">${icon}</span>
                    <p class="text-sm">${message}</p>
                </div>
            `;
        },

        /**
         * Render a list item (for emergency contacts, dependents, etc.)
         * @param {object} options
         * @returns {string}
         */
        listItem(options) {
            const {
                id = '',
                title = '',
                subtitle = '',
                badge = '',
                badgeColor = 'bg-blue-100 text-blue-800',
                onEdit = '',
                onDelete = '',
                content = ''
            } = options;

            return `
                <div class="flex items-start justify-between p-4 border-b border-gray-100 last:border-b-0">
                    <div class="flex-1">
                        <div class="flex items-center gap-2">
                            <span class="font-medium text-gray-900">${title}</span>
                            ${badge ? `<span class="px-2 py-0.5 text-xs rounded-full ${badgeColor}">${badge}</span>` : ''}
                        </div>
                        ${subtitle ? `<p class="text-sm text-gray-500 mt-0.5">${subtitle}</p>` : ''}
                        ${content ? `<div class="mt-2">${content}</div>` : ''}
                    </div>
                    <div class="flex items-center gap-1 ml-4">
                        ${onEdit ? `
                            <button type="button"
                                class="p-1.5 hover:bg-gray-100 rounded-full transition"
                                onclick="${onEdit}"
                                aria-label="${i18n.t('common.edit')}">
                                <span class="material-icons text-gray-500 text-sm">edit</span>
                            </button>
                        ` : ''}
                        ${onDelete ? `
                            <button type="button"
                                class="p-1.5 hover:bg-red-50 rounded-full transition"
                                onclick="${onDelete}"
                                aria-label="${i18n.t('common.delete')}">
                                <span class="material-icons text-red-500 text-sm">delete</span>
                            </button>
                        ` : ''}
                    </div>
                </div>
            `;
        },

        /**
         * Render a "Show More" button
         * @param {string} onclick
         * @param {boolean} expanded
         * @returns {string}
         */
        showMoreButton(onclick, expanded = false) {
            return `
                <button type="button"
                    class="w-full py-2 text-sm text-cg-info hover:text-blue-700 flex items-center justify-center gap-1 transition"
                    onclick="${onclick}">
                    <span>${expanded ? i18n.t('common.showLess') : i18n.t('common.showMore')}</span>
                    <span class="material-icons text-sm ${expanded ? 'rotate-180' : ''}"">expand_more</span>
                </button>
            `;
        }
    };
})();

// Export for module usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = CardComponent;
}

