    } catch (error) {
        console.error('Route execution error:', error);
        AppState.setError(error.message);
    } finally {
        AppState.setLoading(false);
    }

    // Execute after hooks
    for (const hook of afterHooks) {
        await hook(matchedPath, params, previousRoute);
    }
}

