     5→
     6→const API = (function() {
     7→    // Simulated network delay (ms)
     8→    const NETWORK_DELAY = 300;
     9→
    10→    // Simulate network delay
    11→    function delay(ms = NETWORK_DELAY) {
    12→        return new Promise(resolve => setTimeout(resolve, ms));
    13→    }
    14→
    15→    // Generate random ID
    16→    function generateId() {
    17→        return Math.random().toString(36).substring(2, 15);
    18→    }
    19→
    20→    // Deep clone utility
    21→    function clone(obj) {
    22→        return JSON.parse(JSON.stringify(obj));
    23→    }
    24→
    25→    /**
    26→     * Wrapper to manage loading state for async operations
    27→     * @param {Function} asyncFn - Async function to wrap
    28→     * @returns {Function} Wrapped function that manages loading state
    29→     */
    30→    function withLoading(asyncFn) {
    31→        return async function(...args) {
    32→            try {
    33→                AppState.setLoading(true);
    34→                const result = await asyncFn.apply(this, args);
    35→                return result;
    36→            } catch (error) {
    37→                throw error;
    38→            } finally {
    39→                AppState.setLoading(false);
    40→            }
    41→        };
    42→    }
