    92→        },
    93→
    94→        /**
    95→         * Update address information
    96→         * @param {string} employeeId
    97→         * @param {object} data
    98→         * @returns {Promise<object>}
    99→         */
   100→        async updateAddress(employeeId, data) {
   101→            await delay(500);
   102→            const addresses = MockEmployeeData.employee.addresses;
   103→            const index = addresses.findIndex(a => a.addressType === data.addressType);
   104→            if (index !== -1) {
