     1→/**
     2→ * Personal Information Page
     3→ * Personal info, contacts, addresses, emergency contacts, dependents
     4→ */
     5→
     6→const PersonalInfoPage = (function() {
     7→    return {
     8→        /**
     9→         * Render personal information tab
    10→         * @param {object} employee
    11→         * @returns {string}
    12→         */
    13→        render(employee) {
    14→            const isLoading = AppState.get('isLoading');
    15→
    16→            // Show skeleton while loading
    17→            if (isLoading) {
    18→                return this.renderSkeleton();
    19→            }
    20→
    21→            if (!employee) return CardComponent.emptyState(i18n.t('common.noData'));
    22→
    23→            const personal = employee.personalInfo || {};
    24→            const contact = employee.contactInfo || {};
    25→            const addresses = employee.addresses || [];
    26→            const emergencyContacts = employee.emergencyContacts || [];
    27→            const dependents = employee.dependents || [];
    28→
    29→            return `
    30→                <div class="space-y-6">
    31→                    <!-- Personal Information Section -->
    32→                    ${this.renderPersonalInfoSection(personal)}
    33→
    34→                    <!-- Contact Information Section -->
    35→                    ${this.renderContactSection(contact)}
    36→
    37→                    <!-- Address Information Section -->
    38→                    ${this.renderAddressSection(addresses)}
    39→
    40→                    <!-- Emergency Contacts Section -->
    41→                    ${this.renderEmergencyContactsSection(emergencyContacts)}
    42→
    43→                    <!-- Dependents Section -->
    44→                    ${this.renderDependentsSection(dependents)}
    45→                </div>
    46→            `;
    47→        },
    48→
    49→        /**
    50→         * Initialize page
    51→         */
    52→        init() {
    53→            // Any initialization logic
    54→        },
    55→
    56→        /**
    57→         * Render Personal Information section
    58→         */
    59→        renderPersonalInfoSection(personal) {
    60→            const isThai = i18n.isThai();
    61→            const canEdit = RBAC.canEditField('personalInfo', 'firstName');
    62→
    63→            const items = [
    64→                { label: i18n.t('personal.salutationEn'), value: personal.salutationEn },
    65→                { label: i18n.t('personal.salutationTh'), value: personal.salutationTh },
    66→                { label: i18n.t('personal.firstNameEn'), value: personal.firstNameEn },
    67→                { label: i18n.t('personal.firstNameTh'), value: personal.firstNameTh },
    68→                { label: i18n.t('personal.middleNameEn'), value: personal.middleNameEn || '-' },
    69→                { label: i18n.t('personal.lastNameEn'), value: personal.lastNameEn },
    70→                { label: i18n.t('personal.lastNameTh'), value: personal.lastNameTh },
    71→                { label: i18n.t('personal.nickname'), value: personal.nickname || '-' },
    72→                { label: i18n.t('personal.gender'), value: i18n.t(`gender.${personal.gender}`) },
    73→                { label: i18n.t('personal.dateOfBirth'), value: DateUtils.format(personal.dateOfBirth, 'long') },
    74→                { label: i18n.t('personal.nationality'), value: personal.nationality },
    75→                { label: i18n.t('personal.nationalId'), value: MaskUtils.nationalId(personal.nationalId), options: { masked: true } },
    76→                { label: i18n.t('personal.maritalStatus'), value: i18n.t(`maritalStatus.${personal.maritalStatus}`) },
    77→                { label: i18n.t('personal.maritalStatusSince'), value: DateUtils.format(personal.maritalStatusSince, 'medium') || '-' }
    78→            ];
    79→
    80→            return CardComponent.render({
    81→                id: 'personal-info-card',
    82→                title: i18n.t('personal.title'),
    83→                icon: 'person',
    84→                editable: canEdit,
    85→                onEdit: "PersonalInfoPage.editPersonalInfo()",
    86→                showHistory: true,
    87→                onHistory: "PersonalInfoPage.showHistory('personalInfo')",
    88→                effectiveDate: personal.effectiveDate,
    89→                content: CardComponent.dataGrid(items)
    90→            });
    91→        },
    92→
    93→        /**
    94→         * Render Contact Information section
    95→         */
    96→        renderContactSection(contact) {
    97→            const canEdit = RBAC.canEditField('contactInfo', 'personalEmail');
    98→
    99→            const items = [
   100→                { label: i18n.t('personal.businessEmail'), value: contact.businessEmail, options: { icon: 'email' } },
   101→                { label: i18n.t('personal.personalEmail'), value: contact.personalEmail || '-', options: { icon: 'email' } },
   102→                { label: i18n.t('personal.businessPhone'), value: contact.businessPhone || '-', options: { icon: 'phone' } },
   103→                { label: i18n.t('personal.personalMobile'), value: contact.personalMobile, options: { icon: 'smartphone' } },
   104→                { label: i18n.t('personal.homePhone'), value: contact.homePhone || '-', options: { icon: 'home' } }
   105→            ];
   106→
   107→            return CardComponent.render({
   108→                id: 'contact-info-card',
   109→                title: i18n.t('personal.contact'),
   110→                icon: 'contact_phone',
   111→                editable: canEdit,
   112→                onEdit: "PersonalInfoPage.editContactInfo()",
   113→                showHistory: true,
   114→                onHistory: "PersonalInfoPage.showHistory('contactInfo')",
   115→                content: CardComponent.dataGrid(items)
   116→            });
   117→        },
   118→
   119→        /**
   120→         * Render Address Information section
   121→         */
   122→        renderAddressSection(addresses) {
   123→            const canEdit = RBAC.canEditField('address', 'addressLine1');
   124→
   125→            const content = addresses.map(addr => {
   126→                const typeLabelKey = addr.addressType === 'permanent' ? 'permanentAddress' : 'currentAddress';
   127→                const fullAddress = [
   128→                    addr.addressLine1,
   129→                    addr.addressLine2,
   130→                    addr.subDistrict,
   131→                    addr.district,
   132→                    addr.province,
   133→                    addr.postalCode,
   134→                    addr.country
   135→                ].filter(Boolean).join(', ');
   136→
   137→                return `
   138→                    <div class="p-4 bg-gray-50 rounded-lg mb-4 last:mb-0">
   139→                        <div class="flex justify-between items-start mb-2">
   140→                            <span class="font-medium text-gray-900">${i18n.t(`personal.${typeLabelKey}`)}</span>
   141→                            ${canEdit ? `
   142→                                <button class="p-1 hover:bg-gray-200 rounded" onclick="PersonalInfoPage.editAddress('${addr.id}')">
   143→                                    <span class="material-icons text-sm text-gray-500">edit</span>
   144→                                </button>
   145→                            ` : ''}
   146→                        </div>
   147→                        <p class="text-sm text-gray-600">${fullAddress}</p>
   148→                        <p class="text-xs text-gray-400 mt-2">
   149→                            ${i18n.t('common.effectiveDate')}: ${DateUtils.format(addr.effectiveDate, 'medium')}
   150→                        </p>
   151→                    </div>
   152→                `;
   153→            }).join('');
   154→
   155→            return CardComponent.render({
   156→                id: 'address-info-card',
   157→                title: i18n.t('personal.address'),
   158→                icon: 'home',
   159→                showHistory: true,
   160→                onHistory: "PersonalInfoPage.showHistory('addresses')",
   161→                content: content || CardComponent.emptyState(i18n.t('common.noData'))
   162→            });
   163→        },
   164→
   165→        /**
   166→         * Render Emergency Contacts section
   167→         */
   168→        renderEmergencyContactsSection(emergencyContacts) {
   169→            const canAdd = RBAC.hasPermission('add_own_emergency_contacts');
   170→
   171→            const content = emergencyContacts.length > 0
   172→                ? emergencyContacts.map(contact => CardComponent.listItem({
   173→                    id: contact.id,
   174→                    title: contact.name,
   175→                    subtitle: `${i18n.t(`relationship.${contact.relationship}`)} - ${contact.phone}`,
   176→                    badge: contact.isPrimary ? i18n.t('personal.primary') : '',
   177→                    badgeColor: 'bg-green-100 text-green-800',
   178→                    onEdit: `PersonalInfoPage.editEmergencyContact('${contact.id}')`,
   179→                    onDelete: `PersonalInfoPage.deleteEmergencyContact('${contact.id}')`
   180→                })).join('')
   181→                : CardComponent.emptyState(i18n.isThai() ? 'ไม่มีผู้ติดต่อฉุกเฉิน' : 'No emergency contacts');
   182→
   183→            return CardComponent.render({
   184→                id: 'emergency-contacts-card',
   185→                title: i18n.t('personal.emergencyContacts'),
   186→                icon: 'emergency',
   187→                actions: canAdd ? [{
   188→                    icon: 'add',
   189→                    label: i18n.t('personal.addEmergencyContact'),
   190→                    onclick: 'PersonalInfoPage.addEmergencyContact()'
   191→                }] : [],
   192→                content: content
   193→            });
   194→        },
   195→
   196→        /**
   197→         * Render Dependents section
   198→         */
   199→        renderDependentsSection(dependents) {
   200→            const canAdd = RBAC.hasPermission('edit_own_contact');
   201→
   202→            const content = dependents.length > 0
   203→                ? dependents.map(dep => CardComponent.listItem({
   204→                    id: dep.id,
   205→                    title: dep.name,
   206→                    subtitle: `${i18n.t(`relationship.${dep.relationship}`)} - ${DateUtils.format(dep.dateOfBirth, 'medium')}`,
   207→                    onEdit: `PersonalInfoPage.editDependent('${dep.id}')`,
   208→                    onDelete: `PersonalInfoPage.deleteDependent('${dep.id}')`
   209→                })).join('')
   210→                : CardComponent.emptyState(i18n.isThai() ? 'ไม่มีผู้พึ่งพิง' : 'No dependents');
   211→
   212→            return CardComponent.render({
   213→                id: 'dependents-card',
   214→                title: i18n.t('personal.dependents'),
   215→                icon: 'family_restroom',
   216→                actions: canAdd ? [{
   217→                    icon: 'add',
   218→                    label: i18n.t('personal.addDependent'),
   219→                    onclick: 'PersonalInfoPage.addDependent()'
   220→                }] : [],
   221→                content: content
   222→            });
   223→        },
   224→
   225→        // Edit handlers
   226→        editPersonalInfo() {
   227→            const employee = AppState.get('currentEmployee');
   228→            const personal = employee?.personalInfo || {};
   229→
   230→            ModalComponent.open({
   231→                title: i18n.t('personal.editPersonalInfo'),
   232→                size: 'lg',
   233→                content: `
   234→                    <form id="personal-info-form" class="space-y-4">
   235→                        ${FormFieldComponent.date({
   236→                            name: 'effectiveDate',
   237→                            label: i18n.t('common.effectiveDate'),
   238→                            required: true,
   239→                            value: DateUtils.today(),
   240→                            min: DateUtils.today(),
   241→                            hint: i18n.isThai() ? 'วันที่การเปลี่ยนแปลงจะมีผล' : 'When should these changes take effect?'
   242→                        })}
   243→                        <hr class="my-4">
   244→                        <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
   245→                            ${FormFieldComponent.select({
   246→                                name: 'salutationEn',
   247→                                label: i18n.t('personal.salutationEn'),
   248→                                value: personal.salutationEn,
   249→                                options: MockLookupData.salutations.en
   250→                            })}
   251→                            ${FormFieldComponent.select({
   252→                                name: 'salutationTh',
   253→                                label: i18n.t('personal.salutationTh'),
   254→                                value: personal.salutationTh,
   255→                                options: MockLookupData.salutations.th
   256→                            })}
   257→                            ${FormFieldComponent.text({
   258→                                name: 'firstNameEn',
   259→                                label: i18n.t('personal.firstNameEn'),
   260→                                value: personal.firstNameEn,
   261→                                required: true
   262→                            })}
   263→                            ${FormFieldComponent.text({
   264→                                name: 'firstNameTh',
   265→                                label: i18n.t('personal.firstNameTh'),
   266→                                value: personal.firstNameTh
   267→                            })}
   268→                            ${FormFieldComponent.text({
   269→                                name: 'lastNameEn',
   270→                                label: i18n.t('personal.lastNameEn'),
   271→                                value: personal.lastNameEn,
   272→                                required: true
   273→                            })}
   274→                            ${FormFieldComponent.text({
   275→                                name: 'lastNameTh',
   276→                                label: i18n.t('personal.lastNameTh'),
   277→                                value: personal.lastNameTh
   278→                            })}
   279→                            ${FormFieldComponent.text({
   280→                                name: 'nickname',
   281→                                label: i18n.t('personal.nickname'),
   282→                                value: personal.nickname
   283→                            })}
   284→                            ${FormFieldComponent.select({
   285→                                name: 'maritalStatus',
   286→                                label: i18n.t('personal.maritalStatus'),
   287→                                value: personal.maritalStatus,
   288→                                options: MockLookupData.maritalStatuses.map(s => ({
   289→                                    value: s.value,
   290→                                    label: i18n.isThai() ? s.labelTh : s.labelEn
   291→                                }))
   292→                            })}
   293→                        </div>
   294→                    </form>
   295→                `,
   296→                actions: [
   297→                    { label: i18n.t('common.cancel'), onclick: 'ModalComponent.close()' },
   298→                    { label: i18n.t('common.save'), primary: true, onclick: 'PersonalInfoPage.savePersonalInfo()' }
   299→                ]
   300→            });
   301→        },
   302→
   303→        async savePersonalInfo() {
   304→            const formData = FormFieldComponent.getFormData('personal-info-form');
   305→
   306→            // Validate
   307→            const validation = ValidationUtils.validateForm(formData, {
   308→                effectiveDate: { required: true, futureDate: true },
   309→                firstNameEn: { required: true, minLength: 2 },
   310→                lastNameEn: { required: true, minLength: 2 }
   311→            });
   312→
   313→            if (!validation.valid) {
   314→                FormFieldComponent.showErrors(validation.errors);
   315→                ToastComponent.error(i18n.t('error.validationFailed'));
   316→                return;
   317→            }
   318→
   319→            try {
   320→                const employee = AppState.get('currentEmployee');
   321→                await API.updatePersonalInfo(employee.employeeId, formData);
   322→                ToastComponent.success(i18n.t('toast.saveSuccess'));
   323→                ModalComponent.close();
   324→                Router.refresh();
   325→            } catch (error) {
   326→                ToastComponent.error(i18n.t('error.saveFailed'));
   327→            }
   328→        },
   329→
   330→        editContactInfo() {
   331→            const employee = AppState.get('currentEmployee');
   332→            const contact = employee?.contactInfo || {};
   333→
   334→            ModalComponent.open({
   335→                title: i18n.t('personal.editContactInfo'),
   336→                size: 'md',
   337→                content: `
   338→                    <form id="contact-info-form" class="space-y-4">
   339→                        ${FormFieldComponent.text({
   340→                            name: 'personalEmail',
   341→                            label: i18n.t('personal.personalEmail'),
   342→                            value: contact.personalEmail,
   343→                            type: 'email'
   344→                        })}
   345→                        ${FormFieldComponent.text({
   346→                            name: 'personalMobile',
   347→                            label: i18n.t('personal.personalMobile'),
   348→                            value: contact.personalMobile,
   349→                            type: 'tel',
   350→                            required: true
   351→                        })}
   352→                        ${FormFieldComponent.text({
   353→                            name: 'homePhone',
   354→                            label: i18n.t('personal.homePhone'),
   355→                            value: contact.homePhone,
   356→                            type: 'tel'
   357→                        })}
   358→                    </form>
   359→                `,
   360→                actions: [
   361→                    { label: i18n.t('common.cancel'), onclick: 'ModalComponent.close()' },
   362→                    { label: i18n.t('common.save'), primary: true, onclick: 'PersonalInfoPage.saveContactInfo()' }
   363→                ]
   364→            });
   365→        },
   366→
   367→        async saveContactInfo() {
   368→            const formData = FormFieldComponent.getFormData('contact-info-form');
   369→
   370→            const validation = ValidationUtils.validateForm(formData, {
   371→                personalEmail: { email: true },
   372→                personalMobile: { required: true, phone: true }
   373→            });
   374→
   375→            if (!validation.valid) {
   376→                FormFieldComponent.showErrors(validation.errors);
   377→                return;
   378→            }
   379→
   380→            try {
   381→                const employee = AppState.get('currentEmployee');
   382→                await API.updateContactInfo(employee.employeeId, formData);
   383→                ToastComponent.success(i18n.t('toast.saveSuccess'));
   384→                ModalComponent.close();
   385→                Router.refresh();
   386→            } catch (error) {
   387→                ToastComponent.error(i18n.t('error.saveFailed'));
   388→            }
   389→        },
   390→
   391→        addEmergencyContact() {
   392→            this.openEmergencyContactModal();
   393→        },
   394→
   395→        editEmergencyContact(contactId) {
   396→            const employee = AppState.get('currentEmployee');
   397→            const contact = employee?.emergencyContacts?.find(c => c.id === contactId);
   398→            this.openEmergencyContactModal(contact);
   399→        },
   400→
   401→        openEmergencyContactModal(contact = null) {
   402→            const isEdit = !!contact;
   403→
   404→            ModalComponent.open({
   405→                title: isEdit ? i18n.t('common.edit') + ' ' + i18n.t('personal.emergencyContact') : i18n.t('personal.addEmergencyContact'),
   406→                size: 'md',
   407→                content: `
   408→                    <form id="emergency-contact-form" class="space-y-4">
   409→                        <input type="hidden" name="id" value="${contact?.id || ''}">
   410→                        ${FormFieldComponent.text({
   411→                            name: 'name',
   412→                            label: i18n.t('personal.contactName'),
   413→                            value: contact?.name || '',
   414→                            required: true
   415→                        })}
   416→                        ${FormFieldComponent.select({
   417→                            name: 'relationship',
   418→                            label: i18n.t('personal.relationship'),
   419→                            value: contact?.relationship || '',
   420→                            required: true,
   421→                            placeholder: i18n.t('common.select'),
   422→                            options: MockLookupData.relationships.map(r => ({
   423→                                value: r.value,
   424→                                label: i18n.isThai() ? r.labelTh : r.labelEn
   425→                            }))
   426→                        })}
   427→                        ${FormFieldComponent.text({
   428→                            name: 'phone',
   429→                            label: i18n.t('personal.phone'),
   430→                            value: contact?.phone || '',
   431→                            required: true,
   432→                            type: 'tel'
   433→                        })}
   434→                        ${FormFieldComponent.checkbox({
   435→                            name: 'isPrimary',
   436→                            label: i18n.t('personal.isPrimary'),
   437→                            checked: contact?.isPrimary || false
   438→                        })}
   439→                    </form>
   440→                `,
   441→                actions: [
   442→                    { label: i18n.t('common.cancel'), onclick: 'ModalComponent.close()' },
   443→                    { label: i18n.t('common.save'), primary: true, onclick: 'PersonalInfoPage.saveEmergencyContact()' }
   444→                ]
   445→            });
   446→        },
   447→
   448→        async saveEmergencyContact() {
   449→            const formData = FormFieldComponent.getFormData('emergency-contact-form');
   450→
   451→            const validation = ValidationUtils.validateForm(formData, {
   452→                name: { required: true, minLength: 2 },
   453→                relationship: { required: true },
   454→                phone: { required: true, phone: true }
   455→            });
   456→
   457→            if (!validation.valid) {
   458→                FormFieldComponent.showErrors(validation.errors);
   459→                return;
   460→            }
   461→
   462→            try {
   463→                const employee = AppState.get('currentEmployee');
   464→                if (formData.id) {
   465→                    await API.updateEmergencyContact(employee.employeeId, formData.id, formData);
   466→                } else {
   467→                    await API.addEmergencyContact(employee.employeeId, formData);
   468→                }
   469→                ToastComponent.success(i18n.t('toast.saveSuccess'));
   470→                ModalComponent.close();
   471→                Router.refresh();
   472→            } catch (error) {
   473→                ToastComponent.error(i18n.t('error.saveFailed'));
   474→            }
   475→        },
   476→
   477→        async deleteEmergencyContact(contactId) {
   478→            const confirmed = await ModalComponent.confirm({
   479→                title: i18n.t('common.delete'),
   480→                message: i18n.isThai() ? 'คุณต้องการลบผู้ติดต่อฉุกเฉินนี้หรือไม่?' : 'Are you sure you want to delete this emergency contact?',
   481→                confirmText: i18n.t('common.delete'),
   482→                icon: 'delete'
   483→            });
   484→
   485→            if (confirmed) {
   486→                try {
   487→                    const employee = AppState.get('currentEmployee');
   488→                    await API.deleteEmergencyContact(employee.employeeId, contactId);
   489→                    ToastComponent.success(i18n.t('toast.deleteSuccess'));
   490→                    Router.refresh();
   491→                } catch (error) {
   492→                    ToastComponent.error(i18n.t('error.saveFailed'));
   493→                }
   494→            }
   495→        },
   496→
   497→        addDependent() {
   498→            this.openDependentModal();
   499→        },
   500→
   501→        editDependent(dependentId) {
   502→            const employee = AppState.get('currentEmployee');
   503→            const dependent = employee?.dependents?.find(d => d.id === dependentId);
   504→            this.openDependentModal(dependent);
   505→        },
   506→
   507→        openDependentModal(dependent = null) {
   508→            const isEdit = !!dependent;
   509→
   510→            ModalComponent.open({
   511→                title: isEdit ? i18n.t('common.edit') + ' ' + i18n.t('personal.dependent') : i18n.t('personal.addDependent'),
   512→                size: 'md',
   513→                content: `
   514→                    <form id="dependent-form" class="space-y-4">
   515→                        <input type="hidden" name="id" value="${dependent?.id || ''}">
   516→                        ${FormFieldComponent.text({
   517→                            name: 'name',
   518→                            label: i18n.t('personal.dependentName'),
   519→                            value: dependent?.name || '',
   520→                            required: true
   521→                        })}
   522→                        ${FormFieldComponent.select({
   523→                            name: 'relationship',
   524→                            label: i18n.t('personal.relationship'),
   525→                            value: dependent?.relationship || '',
   526→                            required: true,
   527→                            placeholder: i18n.t('common.select'),
   528→                            options: MockLookupData.dependentTypes.map(r => ({
   529→                                value: r.value,
   530→                                label: i18n.isThai() ? r.labelTh : r.labelEn
   531→                            }))
   532→                        })}
   533→                        ${FormFieldComponent.date({
   534→                            name: 'dateOfBirth',
   535→                            label: i18n.t('personal.dateOfBirth'),
   536→                            value: dependent?.dateOfBirth || '',
   537→                            required: true,
   538→                            max: DateUtils.today()
   539→                        })}
   540→                        ${FormFieldComponent.select({
   541→                            name: 'gender',
   542→                            label: i18n.t('personal.gender'),
   543→                            value: dependent?.gender || '',
   544→                            options: MockLookupData.genders.map(g => ({
   545→                                value: g.value,
   546→                                label: i18n.isThai() ? g.labelTh : g.labelEn
   547→                            }))
   548→                        })}
   549→                    </form>
   550→                `,
   551→                actions: [
   552→                    { label: i18n.t('common.cancel'), onclick: 'ModalComponent.close()' },
   553→                    { label: i18n.t('common.save'), primary: true, onclick: 'PersonalInfoPage.saveDependent()' }
   554→                ]
   555→            });
   556→        },
   557→
   558→        async saveDependent() {
   559→            const formData = FormFieldComponent.getFormData('dependent-form');
   560→
   561→            const validation = ValidationUtils.validateForm(formData, {
   562→                name: { required: true, minLength: 2 },
   563→                relationship: { required: true },
   564→                dateOfBirth: { required: true, pastDate: true }
