import { Test, TestingModule } from '@nestjs/testing';
import { INestApplication } from '@nestjs/common';
import { AppModule } from '../../src/app.module';
import { PrismaService } from '../../src/prisma/prisma.service';

/**
 * Integration tests for Organization Service.
 * These tests require a real database connection.
 */
describe('Organization Service Integration', () => {
  let app: INestApplication;
  let prisma: PrismaService;

  beforeAll(async () => {
    const moduleFixture: TestingModule = await Test.createTestingModule({
      imports: [AppModule],
    }).compile();

    app = moduleFixture.createNestApplication();
    await app.init();

    prisma = app.get<PrismaService>(PrismaService);
  });

  afterAll(async () => {
    await app.close();
  });

  beforeEach(async () => {
    await prisma.$executeRaw\`DELETE FROM "incumbents" WHERE employee_id LIKE 'TEST_%'\`;
    await prisma.$executeRaw\`DELETE FROM "transfers" WHERE employee_id LIKE 'TEST_%'\`;
    await prisma.$executeRaw\`DELETE FROM "reporting_lines" WHERE from_employee_id LIKE 'TEST_%'\`;
    await prisma.$executeRaw\`DELETE FROM "org_nodes" WHERE employee_id LIKE 'TEST_%'\`;
    await prisma.$executeRaw\`DELETE FROM "positions" WHERE id LIKE 'TEST_%'\`;
    await prisma.$executeRaw\`DELETE FROM "departments" WHERE id LIKE 'TEST_%'\`;
  });

  it('should create and retrieve a department', async () => {
    const dept = await prisma.department.create({
      data: {
        id: 'TEST_D001',
        code: 'TEST_30040490',
        name_en: 'Test Department',
        name_th: 'แผนกทดสอบ',
        company_id: 'C015',
      },
    });

    expect(dept.id).toBe('TEST_D001');

    const found = await prisma.department.findUnique({ where: { id: 'TEST_D001' } });
    expect(found).toBeDefined();
    expect(found!.name_en).toBe('Test Department');
  });

  it('should create a position linked to department', async () => {
    await prisma.department.create({
      data: {
        id: 'TEST_D002',
        code: 'TEST_30040491',
        name_en: 'Test Dept 2',
        company_id: 'C015',
      },
    });

    const position = await prisma.position.create({
      data: {
        id: 'TEST_POS001',
        position_code: 'TEST_40128307',
        title_en: 'Test Position',
        department_id: 'TEST_D002',
        company_id: 'C015',
        status: 'active',
      },
    });

    expect(position.id).toBe('TEST_POS001');

    const withDept = await prisma.position.findUnique({
      where: { id: 'TEST_POS001' },
      include: { department: true },
    });

    expect(withDept!.department.name_en).toBe('Test Dept 2');
  });

  it('should create org nodes and reporting lines', async () => {
    await prisma.orgNode.create({
