'use client';

import { useTranslations } from 'next-intl';
import {
  User,
  Calendar,
  FileText,
  ClipboardList,
  Users,
  Calculator,
} from 'lucide-react';
import { Header } from '@/components/shared/header';
import { Sidebar } from '@/components/shared/sidebar';
import { MobileMenu } from '@/components/shared/mobile-menu';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { useAuthStore } from '@/stores/auth-store';
import { isManager, isHR } from '@/lib/rbac';

interface QuickAction {
  title: string;
  description: string;
  href: string;
  icon: React.ReactNode;
  color: string;
}

export default function HomePage() {
  const t = useTranslations();
  const { username, roles } = useAuthStore();

  const quickActions: QuickAction[] = [
    {
      title: t('home.viewMyProfile'),
      description: t('home.viewMyProfileDesc'),
      href: '/profile',
      icon: <User className="h-6 w-6" />,
      color: 'text-blue-600 bg-blue-50',
    },
    {
      title: t('leave.title'),
      description: t('leave.request'),
      href: '/leave',
      icon: <Calendar className="h-6 w-6" />,
      color: 'text-green-600 bg-green-50',
    },
    {
      title: t('payslip.title'),
      description: t('payslip.viewDetails'),
      href: '/payslip',
      icon: <FileText className="h-6 w-6" />,
      color: 'text-purple-600 bg-purple-50',
    },
    {
      title: t('home.viewPendingWorkflows'),
      description: t('home.viewPendingWorkflowsDesc'),
      href: '/workflows',
      icon: <ClipboardList className="h-6 w-6" />,
      color: 'text-orange-600 bg-orange-50',
    },
  ];

  const managerActions: QuickAction[] = [
    {
      title: t('home.teamSummary'),
      description: t('home.directReports'),
      href: '/manager-dashboard',
      icon: <Users className="h-6 w-6" />,
      color: 'text-cg-red bg-red-50',
    },
  ];

  const hrActions: QuickAction[] = [
    {
      title: t('payroll.title'),
      description: t('payroll.subtitle'),
      href: '/payroll-processing',
      icon: <Calculator className="h-6 w-6" />,
      color: 'text-indigo-600 bg-indigo-50',
    },
  ];

  const allActions = [
    ...quickActions,
    ...(isManager(roles) ? managerActions : []),
    ...(isHR(roles) ? hrActions : []),
  ];

  return (
    <div className="min-h-screen bg-cg-light">
      <Header />
      <MobileMenu />
      <div className="flex">
        <Sidebar />
        <main className="flex-1 p-6">
          <div className="mb-8">
            <h1 className="text-2xl font-bold text-cg-dark">
              {t('home.welcome')}, {username || 'User'}
            </h1>
            <p className="text-gray-500 mt-1">{t('home.forYouToday')}</p>
          </div>

          <section>
            <h2 className="text-lg font-semibold text-cg-dark mb-4">
              {t('home.quickActions')}
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {allActions.map((action) => (
                <a key={action.href} href={action.href}>
                  <Card className="hover:shadow-md transition-shadow cursor-pointer h-full">
                    <CardHeader className="pb-3">
                      <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${action.color}`}>
                        {action.icon}
                      </div>
                    </CardHeader>
                    <CardContent>
                      <CardTitle className="text-base mb-1">{action.title}</CardTitle>
                      <p className="text-sm text-gray-500">{action.description}</p>
                    </CardContent>
                  </Card>
                </a>
              ))}
            </div>
          </section>
        </main>
      </div>
    </div>
  );
}

import { NextIntlClientProvider, hasLocale } from 'next-intl';
import { notFound } from 'next/navigation';
import { routing } from '@/i18n/routing';

export default async function LocaleLayout({
  children,
  params,
}: {
  children: React.ReactNode;
  params: Promise<{ locale: string }>;
}) {
  const { locale } = await params;

  if (!hasLocale(routing.locales, locale)) {
    notFound();
  }

  const messages = (await import(`../../../messages/${locale}.json`)).default;

  return (
    <html lang={locale}>
      <body>
        <NextIntlClientProvider locale={locale} messages={messages}>
          {children}
        </NextIntlClientProvider>
      </body>
    </html>
  );
}

import NextAuth from 'next-auth';
import Keycloak from 'next-auth/providers/keycloak';

declare module 'next-auth' {
  interface Session {
    accessToken?: string;
    user: {
      id: string;
      name?: string | null;
      email?: string | null;
      image?: string | null;
      roles: string[];
    };
  }
}

declare module 'next-auth' {
  interface JWT {
    accessToken?: string;
    roles?: string[];
  }
}

export const { handlers, signIn, signOut, auth } = NextAuth({
  providers: [
    Keycloak({
      clientId: process.env.KEYCLOAK_CLIENT_ID ?? 'hrms-frontend',
      clientSecret: process.env.KEYCLOAK_CLIENT_SECRET ?? '',
      issuer:
        process.env.KEYCLOAK_ISSUER ??
        'http://localhost:8080/realms/central-hrms',
    }),
  ],
  callbacks: {
    async jwt({ token, account }) {
      if (account) {
        token.accessToken = account.access_token;
        // Extract roles from Keycloak realm_access
        const decoded = account.access_token
          ? JSON.parse(
              Buffer.from(account.access_token.split('.')[1], 'base64').toString()
            )
          : {};
        token.roles = decoded.realm_access?.roles ?? [];
      }
      return token;
    },
    async session({ session, token }) {
      session.accessToken = token.accessToken as string | undefined;
      session.user.roles = (token.roles as string[]) ?? [];
      return session;
    },
  },
  pages: {
    signIn: '/auth/signin',
  },
});

import { getSession } from 'next-auth/react';

const BASE_URL = process.env.NEXT_PUBLIC_API_URL ?? 'http://localhost:4000/api';
const MAX_RETRIES = 2;
const RETRY_DELAY = 1000;

export interface ApiError {
  status: number;
  message: string;
  details?: unknown;
}

async function getAuthHeaders(): Promise<HeadersInit> {
  const session = await getSession();
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
  };
  if (session?.accessToken) {
    headers['Authorization'] = `Bearer ${session.accessToken}`;
  }
  return headers;
}

async function request<T>(
  method: string,
  path: string,
  body?: unknown,
  retries = MAX_RETRIES
): Promise<T> {
  const url = `${BASE_URL}${path}`;
  const headers = await getAuthHeaders();

  const init: RequestInit = { method, headers };
  if (body) {
    init.body = JSON.stringify(body);
  }

  try {
    const res = await fetch(url, init);

    if (!res.ok) {
      const error: ApiError = {
        status: res.status,
        message: res.statusText,
      };
      try {
        error.details = await res.json();
      } catch {}

      if (res.status >= 500 && retries > 0) {
        await new Promise((r) => setTimeout(r, RETRY_DELAY));
        return request<T>(method, path, body, retries - 1);
      }

      throw error;
    }

    if (res.status === 204) return undefined as T;
    return res.json();
  } catch (err) {
    if ((err as ApiError).status) throw err;

    if (retries > 0) {
      await new Promise((r) => setTimeout(r, RETRY_DELAY));
      return request<T>(method, path, body, retries - 1);
    }
    throw { status: 0, message: 'Network error' } as ApiError;
  }
}

export const api = {
  get: <T>(path: string) => request<T>('GET', path),
  post: <T>(path: string, body?: unknown) => request<T>('POST', path, body),
  patch: <T>(path: string, body?: unknown) => request<T>('PATCH', path, body),
  delete: <T>(path: string) => request<T>('DELETE', path),
};

export interface Employee {
  id: string;
  employeeId: string;
  firstName: string;
  lastName: string;
  firstNameTh?: string;
  lastNameTh?: string;
  nickname?: string;
  email: string;
  position: string;
  department: string;
  company: string;
  location: string;
  hireDate: string;
  status: 'active' | 'inactive' | 'terminated';
  supervisorId?: string;
  avatarUrl?: string;
}

export interface PersonalInfo {
  salutation?: string;
  firstName: string;
  middleName?: string;
  lastName: string;
  firstNameTh?: string;
  lastNameTh?: string;
  nickname?: string;
  gender: 'male' | 'female' | 'other';
  dateOfBirth: string;
  nationality: string;
  nationalId: string;
  maritalStatus: 'single' | 'married' | 'divorced' | 'widowed';
  maritalStatusSince?: string;
  religion?: string;
  bloodType?: string;
  militaryStatus?: string;
}

export interface ContactInfo {
  businessEmail: string;
  personalEmail?: string;
  businessPhone?: string;
  personalMobile?: string;
  homePhone?: string;
}

export interface Address {
  type: 'permanent' | 'current';
  addressLine1: string;
  addressLine2?: string;
  subDistrict: string;
  district: string;
  province: string;
  postalCode: string;
  country: string;
}

export interface EmergencyContact {
  id: string;
  name: string;
  relationship: string;
  phone: string;
  isPrimary: boolean;
}

export interface Dependent {
  id: string;
  name: string;
  type: 'spouse' | 'child' | 'parent';
  dateOfBirth?: string;
  nationalId?: string;
}

export interface WorkPermit {
  permitNumber: string;
  permitType: string;
  issueDate: string;
  expiryDate: string;
  issuingAuthority: string;
  status: 'active' | 'expired' | 'expiring';
}

