      form: 'PVD_MONTHLY',
      year,
      month,
      total_employees: entries.length,
      total_pvd_employee: entries.reduce((sum: number, e: any) => sum + e.pvd_employee, 0),
      total_pvd_employer: entries.reduce((sum: number, e: any) => sum + e.pvd_employer, 0),
      entries,
    };
  }
}
