      // 2,000,000 => 115,000 + (1M * 25%) = 365,000
      expect(ThaiPitCalculator.calculateAnnualTax(2000000)).toBe(365000);
      // 3,000,000 => 365,000 + (1M * 30%) = 665,000
      expect(ThaiPitCalculator.calculateAnnualTax(3000000)).toBe(665000);
    });

