      await service.postConfirm('ot-1', 3, manager as any);
      expect(mockPrisma.overtimeRequest.update).toHaveBeenCalledWith({
        where: { id: 'ot-1' },
        data: expect.objectContaining({
          hours: 3,
          amount: 1200, // 3 * 200 * 2.0
          total_amount: 1200,
        }),
      });
